INSERT INTO `news` (`id`, `title_ru`, `title_kk`, `title_en`, `img`, `sh_desc_ru`, `sh_desc_kk`, `sh_desc_en`, `desc_ru`, `desc_kk`, `desc_en`, `view`, `is_archive`, `status`, `created_at`, `updated_at`) VALUES
(50, '7-ая международная конференция Всемирного Форума Закят', 'Дүниежүзілік Зекет форумының 7-ші халықаралық конференциясы', 'The 7th International Conference of the World Zakat Forum', 'images/uploads/52ad0c40ae7372677e9b79bfbd67b717.jpg', 'Президент Фонда Кайрат Азимов выступил с речью на тему «Закят и Цели устойчивого развития» с сопроводительной презентацией Фонда.', '«Зекет және Орнықты дамудың мақсаттары» тақырыбында баяндама жасалып, «УАҚЫП» Қазақстандық қайырымдылық қорының» презентациясы өткізілді.', 'President of the fund Kayrat Azimov delivered a speech on the topic of «Zakat and Sustainable Development Goals», introducing the Fund’s presentation.', '<p style=\"text-align: justify;\">5-6 декабря т.г. в городе Малака (Малайзия) состоялась 7-ая международная конференция Всемирного Форума Закят. Данное мероприятие было посвящено укреплению всемирного закят сотрудничества с целью улучшения благосостояния мусульманской уммы. В конференции приняли участие свыше 300 делегаций от 70 стран, в рамках которой Президент Фонда Кайрат Азимов выступил с речью на тему &laquo;Закят и Цели устойчивого развития&raquo; с сопроводительной презентацией Фонда.</p>', '<p style=\"text-align: justify;\">Ү.ж. 5-6 желтоқсанда Малака қаласында (Малайзия) Дүниежүзілік Зекет форумының 7-ші халықаралық конференциясы өтті. Іс-шараның басты тақырыбы - Мұсылман үмметінің әл-ауқатын арттырудағы Зекеттің жаһандық ынтымақтастығын күшейту. Конференция жұмысына 70 жуық елден 300 астам делегат қатысты.</p>\r\n\r\n<p style=\"text-align: justify;\">Оның шеңберінде &laquo;Зекет және Орнықты дамудың мақсаттары&raquo; тақырыбында баяндама жасалып, &laquo;УАҚЫП&raquo; Қазақстандық қайырымдылық қорының&raquo; презентациясы өткізілді.</p>', '<p style=\"text-align: justify;\">The 7th International Conference of the World Zakat Forum was held in Melaka (Malaysia) on 5-6 December. The main topic of the event is &laquo;Strengthening Global Zakat Cooperation in the Welfare of the Ummah&raquo;. The conference was attended by over 300 delegates from over 70th countries. Within the framework of them speech, the President of the fund Kayrat Azimov delivered a speech on the topic of &laquo;Zakat and Sustainable Development Goals&raquo;, introducing the Fund&rsquo;s presentation.</p>', 1, 0, 1, '2018-12-05 21:29:00', '2019-05-10 09:33:20'),
(51, 'Встреча с Общественным фондом «Дом мамы»', '«Ана үйімен» кездесу', 'Meeting with the Social Fund - «Mother’s house»', 'images/uploads/07c0e4ef48d3c617062ef308a12bf37c.jpg', 'Меморандум о взаимопонимании и сотрудничестве между Казахстанским благотворительным фондом «Уакып» и Общественным фондом «Дом мамы».', 'Қос Қордың басшылары келіскен бастамалар шеңберінде жан-жақты өзара қолғабыс көрсетуге дайын екендерін жеткізді.', 'The meeting issued the Memorandum of Understanding and Cooperation.', '<p style=\"text-align: justify;\">17 января т.г. президент Фонда К.Азимов посетил Общественный фонд &laquo;Дом мамы&raquo; и встретился с коллективом данной организации. В рамках встречи К.Азимов ознакомился с социальными проектами общественного фонда. Руководство &laquo;Дом мамы&raquo; рассказали о своих направлениях в рамках создания Национального агенства по усыновлению на базе проекта &laquo;Дом мамы&raquo;. По итогам встречи был подписан Меморандум о взаимопонимании и сотрудничестве между Казахстанским благотворительным фондом &laquo;Уакып&raquo; и Общественным фондом &laquo;Дом мамы&raquo;.</p>', '<p style=\"text-align: justify;\">Ү.ж. 17 қаңтарда, Қ.Әзімов &laquo;Ана үйі&raquo; жобасымен және осы жобаның басында жүрген жауапты адамдардың жұмыстарымен танысу мақсатында, &laquo;Ана үйі&raquo; қоғамдық қорына барды. Кездесу барысында құрылуы жоспарланып отырған балаларды асырап алу жөніндегі ұлттық агенттіктің жүйесі таныстырылды. Кездесу соңында &laquo;Уақып&raquo; Қазақстандық қайырымдылық қоры және &quot;Ана үйі&quot; қоғамдық қоры арасындағы өзара түсіністік пен ынтымақтастық туралы меморандумға қол қойылды. Қос Қордың басшылары келіскен бастамалар шеңберінде жан-жақты өзара қолғабыс көрсетуге дайын екендерін жеткізді.</p>', '<p style=\"text-align: justify;\">The president of the fund Kayrat Azimov visited the Social Fund - &laquo;Mother&rsquo;s house&raquo; and met with the staff of the organization on the 17<sup>th</sup>&nbsp;of January. In the frame of the meeting Kayrat Azimov got familiarized with the projects of the Organization. The management of the organization talked of their efforts towards the establishment of the National adoption agency the on the basis of the draft &laquo;Mother&rsquo;s house&raquo;. The meeting issued the Memorandum of Understanding and Cooperation.</p>', 1, 0, 1, '2019-01-17 03:34:00', '2019-05-10 09:36:04'),
(52, 'Встреча с Центром независимой жизни «ДОС»', 'Қайрат Әзімов «ДОС» ұйымына алғыс білдірлі', 'Meeting with the Centre of independent living «DOS»', 'images/uploads/a71af202d68ce8b80b22b1582b249d89.jpg', 'По итогам работы с Центром «ДОС» была проведена встреча, где определились перспективы совместных проектов.', 'Жиын соңында Қор ДОС ұйымымен ортақ жобаларды атқаруға уағдаласты.', 'President and the Centre’s representatives in order to determine the prospects of joint projects.', '<p style=\"text-align: justify;\">По регионам страны были распределены инвалидные коляски при участии нашего Фонда. За содействие в осуществлении этой задачи Кайрат Азимов от имени Фонда выразил особую благодарность Общественному объединению инвалидов, Центру независимая жизнь &laquo;ДОС&raquo;. По итогам работы с Центром &laquo;ДОС&raquo; была проведена встреча, где определились перспективы совместных проектов.</p>', '<article>\r\n<p style=\"text-align: justify;\">Қордың қатысуымен мүгедектерге арналған арбаларды Қазақстанның өңірлеріне тарату шарасы аяқталды. Қайрат Азимов Қор атынан аталған шараны іске асыруға үлес қосқан Қазақстандық ДОС ұйымына алғыс білдірді. Жиын соңында Қор ДОС ұйымымен ортақ жобаларды атқаруға уағдаласты.</p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n</article>', '<p>Wheelchairs were distributed to a number of regions of the Kazakhstan with the participation of the fund. On behalf of the fund Kayrat Azimov expressed the gratitude to the Centre of independent living &laquo;DOS&raquo; for the support provided in pursuing the charity mission. On the basis of work with the Centre, a meeting was held between the fund&rsquo;s President and the Centre&rsquo;s representatives in order to determine the prospects of joint projects.</p>', 1, 0, 1, '2019-01-19 07:28:00', '2019-05-23 06:47:33'),
(53, 'Встреча с заслуженными артистами Казахстана', 'Қазақстанның еңбек сіңірген қайраткерлерімен кездесу', 'Meeting with honored artists of Kazakhstan', 'images/uploads/709d87dce33d4ab68c806f3dacc8058a.jpg', 'Состоялась встреча Кайрата Азимова с заслуженными артистами Казахстана', 'Қайрат Әзімов Қазақстанның еңбек сіңірген қайраткерлері', 'Kayrat Azimov met with Rakhman Omar and Saken Maigaziyev, honored artists of Kazakhstan.', '<p>Состоялась встреча Кайрата Азимова с заслуженными артистами Казахстана Рахманом Омаров и Сакеном Майгазиевым в городе Алматы. В ходе встречи представители культуры проявили интерес к деятельности фонда и готовность поддержать начинания.</p>', '<p>Қайрат Әзімов Қазақстанның еңбек сіңірген қайраткерлері Рахман Омар мен Сәкен Майғазиевпен кездесті. Кездесу барысында мәдениет өкілдері Қор жұмысына қызығушылық танытып, бастамаларды қолдауға дайындықтарын білдірді.</p>', '<p>Kayrat Azimov met with Rakhman Omar and Saken Maigaziyev, honored artists of Kazakhstan. The meeting took place in the informal settings in Almaty. The artistic aesthetes have expressed interest in the fund&rsquo;s work and indicated their readiness to support the undertakings.</p>', 1, 0, 1, '2019-01-25 07:30:00', '2019-05-23 06:47:23'),
(54, 'Азимов Кайрат в программе «Таң Шолпан»', 'Қайрат Әзімов «Таң Шолпан» бағдарламасында', NULL, 'images/uploads/bc1ef780926100a362d485938ffa5e3a.png', 'Программа «Таң Шолпан» является открывающей в эфире Национального телеканала «Қазахстан», выходит в эфир с понедельника по пятницу с 07:00 до 10:00 утра. ', 'Әр таң сайын «Қазақстан» Ұлттық арнасының шымылдығын ашатын «Таңшолпан» бағдарламасы, дүйсенбіден жұмаға дейін, таңғы сағат 07:00 ден 10:00 дейін көрсетіледі. ', NULL, '<p style=\"text-align: justify;\">Участие Азимова Кайрата в программе &laquo;Таң Шолпан&raquo; на Национальном телеканале Qazaqstan, где президент фонда коротко осветил проекты, реализуемые в рамках работы фонда &laquo;Уақып&raquo;. Отличные вопросы задавали ведущие, в том числе какая ведется работа с молодежью в связи с объявленным годом молодежи в Казахстане. Кайрат Муратович выразил свое мнение и позицию фонда по направлениям, где силами фонда оказывается поддержка молодежи в образовании и трудоустройстве.</p>\r\n\r\n<p style=\"text-align: center;\"><iframe allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"310\" src=\"https://www.youtube.com/embed/c3Mv181LgRY\" width=\"556\"></iframe></p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Программа &laquo;Таң Шолпан&raquo; является открывающей в эфире Национального телеканала &laquo;Қазахстан&raquo;, выходит в эфир с понедельника по пятницу с 07:00 до 10:00 утра.&nbsp;</p>', '<p style=\"text-align: justify;\">&laquo;Қазақстан&raquo; Ұлттық телеарнасында өтетін, Әзімов Қайраттың &laquo;Таң Шолпан&raquo; бағдарламасына қатысқан видео сұхбаты. Сұхбат кезінде &laquo;Уақып&raquo; қорының президенті келешек жобаларға қысқаша баяндама беріп жүргізушілердің қызық сұрақтарына жауап берді. Сонымен бірге Жастар Жылына байланысты, Қайрат Мұратұлы өз пікірімен бөлісіп, фонд жұмысының шеңберінде жастармен қандай бағытта жұмыс атқарылатынын айтып шықты.</p>\r\n\r\n<p style=\"text-align: center;\"><iframe allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"310\" src=\"https://www.youtube.com/embed/c3Mv181LgRY\" width=\"556\"></iframe></p>\r\n\r\n<p style=\"text-align: justify;\">Әр таң сайын &laquo;Қазақстан&raquo; Ұлттық арнасының шымылдығын ашатын &laquo;Таңшолпан&raquo; бағдарламасы, дүйсенбіден жұмаға дейін, таңғы сағат 07:00 ден 10:00 дейін көрсетіледі.&nbsp;</p>', '', 1, 0, 1, '2019-02-05 07:33:00', '2019-05-11 13:38:33'),
(55, 'Встреча с  с Заместителем акима города Алматы Арманом Кырыкбаевым', 'Алматы қ. әкімінің орынбасары Арман Қырықбаевпен кездесу', 'A meeting with the Deputy governor of Almaty, Arman Kyrykbayev', 'images/uploads/3076565223ff83f23ad8fac83e85269e.jpg', 'В xоде встречи были обсуждены вопросы по планируемым социальным проектам Фонда.', 'Кездесу барысында Қордың әлеуметтік тұрғыдағы жобалары талқыланды.', 'The President of the Fund Kayrat Azimov addressed the issues of the future social projects.', '<article>\r\n<p style=\"text-align: justify;\">9 февраля т.г. Президент Фонда Кайрат Азимов провел встречу с Заместителем акима города Алматы Арманом Кырыкбаевым. В xоде встречи были обсуждены вопросы по планируемым социальным проектам Фонда. Заместитель акима, в свою очередь, выразил готовность в оказании соответсвующей помощи в иx реализации.</p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n</article>', '<p style=\"text-align: justify;\">&nbsp;Ү.ж. 9 ақпанда Қор президенті Қайрат Әзімов Алматы қаласы әкімінің орынбасары Арман Қырықбаевпен кездесті. Кездесу барысында Қордың әлеуметтік тұрғыдағы жобалары талқыланды. Әкім орынбасары, өз кезегінде, Қордың әлеуметтік сипаттағы жоспарларының іске асырылуына тиісті көмек көрсететінін айтты.</p>', '<p style=\"text-align: justify;\">A meeting with Arman Kyrykbayev, the Deputy governor of Almaty took place on 9 February this year. The President of the Fund Kayrat Azimov addressed the issues of the future social projects. The Deputy governor, in turn, expressed his willingness to assist in their realization.</p>', 1, 0, 1, '2019-02-09 07:39:00', '2019-05-23 06:47:15'),
(56, 'Встреча Кайрата Азимова с трехкратным чемпионом мира по боям без правил Ардаком Назаровым', 'Қайрат Әзімов жекпе-жектен үш дүркін әлем чемпионы Ардақ Назаровпен кездесті', 'A meeting with the three-time MMA world champion Ardak Nazarov', 'images/uploads/5f71b4525b5e0b1c00c3de9ae43b4102.jpg', 'По итогам встречи была достигнута договоренность о вхождении Ардака Назарова в состав Попечительского совета Фонда.', 'Кездесудің нәтижесінде қоғам қайраткері Ардақ Назаров Қор Қарқоршылар кеңесінің құрамына кіруге келісті.', 'As a result of the meeting, it was agreed that Ardak Nazarov would join the Board of the Trustees of the Fund.', '<p style=\"text-align: justify;\">В Алматы состоялась деловая встреча Президента Фонда Кайрата Азимова с трехкратным чемпионом мира по боям без правил и общественным деятелем Ардаком Назаровым в неформальной обстановке.</p>\r\n\r\n<p style=\"text-align: justify;\">По итогам встречи была достигнута договоренность о вхождении Ардака Назарова в состав Попечительского совета Фонда.</p>', '<p style=\"text-align: justify;\">Алматы қаласында Қор президенті Қайрат Әзімов аралас жекпе-жектен үш дүркін әлем чемпионы Ардақ Назаровпен кездесті.</p>\r\n\r\n<p style=\"text-align: justify;\">Кездесудің нәтижесінде қоғам қайраткері Ардақ Назаров Қор Қарқоршылар кеңесінің құрамына кіруге келісті.</p>', '<p style=\"text-align: justify;\">Last week the President of the Fund Kayrat Azimov held a business meeting in informal settings with the three-time MMA world champion Ardak Nazarov.</p>\r\n\r\n<p style=\"text-align: justify;\">As a result of the meeting, it was agreed that Ardak Nazarov would join the Board of the Trustees of the Fund.</p>', 1, 0, 1, '2019-02-09 07:41:00', '2019-05-23 06:47:06'),
(57, 'Встреча с руководителями Египетского Университета исламской культуры «Нур-Мубарак»', 'Нұр-Мүбарак Мысыр ислам мәдениеті университетінің басқармасымен кездесу', 'A meeting with the administration of the Egyptian University of the Islamic Culture Nur-Mubarak', 'images/uploads/d00c015548e40274047f806d4b1bc5bb.jpg', 'С представителями ВУЗа обсудили совместную работу с Фондом, в том числе потенциал ВУЗа, повышение квалификации и количества преподавателей.', 'Кездесу барысында ЖОО өкілдері Қормен бірлескен жұмысын, сондай-ақ оқу орынның әлеуетін, тәлімгерлердің білімі мен санын арттыруын талқылады.', 'During the meeting the sides discussed the joint work plan, namely the potential of the University, further training and increase in the number of tutors.', '<p style=\"text-align: justify;\">Состоялась встреча Кайрата Азимова с руководителями Египетского Университета исламской культуры &laquo;Нур-Мубарак&raquo;.</p>\r\n\r\n<p style=\"text-align: justify;\">С представителями ВУЗа обсудили совместную работу с Фондом, в том числе потенциал ВУЗа, повышение квалификации и количества преподавателей.</p>\r\n\r\n<p style=\"text-align: justify;\"><em>Справочно:&nbsp;</em><em>Фонд</em>&nbsp;<em>тесно сотрудничает с&nbsp;</em><em>У</em><em>ниверситетом по нескольким проектам, в&nbsp;</em><em>частности по</em><em>&nbsp;направлени</em><em>ю</em><em>&nbsp;&laquo;Работа с имам</em><em>ам</em><em>и&raquo;</em><em>.</em>&nbsp;<em>Н</em><em>а ежегодной основе предоставляются гранты на обучение в Университете для студентов из малообеспеченных семей.</em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://waqyp.kz/images/2019/IMG_7770.JPG\" /></p>', '<p style=\"text-align: justify;\">Қайрат Әзімов Алматы қ. Нұр-Мүбарак Мысыр ислам мәдениеті университетінің басқармасымен кездесті. Кездесу барысында ЖОО өкілдері Қормен бірлескен жұмысын, сондай-ақ оқу орынның әлеуетін, тәлімгерлердің білімі мен санын арттыруын талқылады.</p>\r\n\r\n<p style=\"text-align: justify;\"><em>Анықтама: Қор&nbsp;</em><em>У</em><em>ниверсит</em><em>пен</em><em>&nbsp;бірнеше проекттер, атап айтқанда &laquo;Имамдармен жұмыс&raquo;</em>&nbsp;<em>бағыты бойынша тығыз қызмет істеп келеді.</em>&nbsp;<em>Әлеуметтік жағдайы төмен отбасыдан шыққан студенттерге аталған Университке оқу үшін жыл сайын гранттар бөлінеді.</em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://waqyp.kz/images/2019/IMG_7770.JPG\" /></p>', '<p style=\"text-align: justify;\">A meeting was held between Kayrat Azimov and the administration of the Egyptian University of the Islamic Culture Nur-Mubarak in Almaty. During the meeting the sides discussed the joint work plan, namely the potential of the University, further training and increase in the number of tutors.</p>\r\n\r\n<p style=\"text-align: justify;\"><em>For reference: The fund works closely with the University on several projects, namely in the direction &laquo;Work with imams&raquo;. Students from low-income families get scholarships to study in the University an annual basis.</em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://waqyp.kz/images/2019/IMG_7770.JPG\" /></p>', 1, 0, 1, '2019-02-10 07:44:00', '2019-05-23 06:46:59'),
(58, 'Встреча с директором института Востоковедения Абсаттар Хаджи Дербисали', 'Шығыстану институтының директоры Әбсаттар қажы Дербісәлімен кездесу', 'Kayrat Azimov met with Absattar Derbisali, the Director of the Institute of Oriental Studies', 'images/uploads/65ec283e9d14e2890e3d7ef5d3da6156.jpg', 'Хаджи на протяжении долгих лет служит в образовании и внес лепту в развитие религии в нашей стране, выразил поддержку и пожелал успехов в начинаниях президенту фонда «Уакып».', 'Кездесу барысында Қордың жұмыстары талқыланды.', 'During the meeting, there had been a discussion on issues pertaining to the the fund’s work.', '<p style=\"text-align: justify;\">В рамках рабочей командировки в город Алматы, Азимов Кайрат провел встречу с директором института Востоковедения Абсаттар Хаджи Дербисали, где были обсуждены рабочие вопросы фонда. Хаджи на протяжении долгих лет служит в образовании и внес лепту в развитие религии в нашей стране, выразил поддержку и пожелал успехов в начинаниях президенту фонда &laquo;Уакып&raquo;.</p>', '<p>ҚР Білім және ғылым министрлігі жанындағы Шығыстану институтының директоры Әбсаттар қажы Дербісәлімен кездесу өтті. Кездесу барысында Қордың жұмыстары талқыланды.</p>', '<article>\r\n<p style=\"text-align: justify;\">A meeting was held between Absattar Derbisali, the Director of the Institute of Oriental Studies under the Ministry of Education and Science of the Republic of Kazakhstan and Kayrat Azimov. During the meeting, there had been a discussion on issues pertaining to the the fund&rsquo;s work.</p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n</article>', 1, 0, 1, '2019-02-11 07:47:00', '2019-05-23 06:46:51'),
(59, 'Благотворительная акция «Мың алғыс»', '«Мың алғыс» қайырымдылық шарасы', 'The charitable action «Myn algys»', 'images/uploads/03b9a3912d74cba56d3e1ddc4b82f6ef.jpg', 'Собранные средства будут вручены нуждающимся помощи людям во время концерта «Мың алғыс!». Желающие совершить доброе дело могут направляют свои средства в наш Фонд.', '«МЫҢ АЛҒЫС» атты қайырымдылық шарасының аясында 2019 жылғы 11-28 ақпан аралығында өтіп жатқан «Қайырымдылықтан қалыс қалма! Алғысқа бөлен!» радио-видеомарафонына қатысуға шақырамыз.', 'The funds raised will be handed over to the needy people during the concert «Myn algys!». People willing to to a good deed can allocate funds to our Fund.', '<p style=\"text-align: justify;\">С&nbsp;<strong>11 по 28 февраля&nbsp;</strong>проxодит благотворительная акция товарищества &laquo;Qazaq Radiolary&raquo; под названием &laquo;Мың алғыс&raquo;. &laquo;Shalqar&raquo;, &laquo;Астана&raquo;, &laquo;Classic&raquo; и &laquo;Qazaq radiosy&raquo; при товариществе &laquo;Qazaq Radiolary&raquo; пригласят в студию известных общественных деятелей, ценителей искусства, меценатов и проведут агитацию за благотворительность.</p>\r\n\r\n<p style=\"text-align: justify;\">Собранные средства будут вручены нуждающимся помощи людям во время концерта &laquo;Мың алғыс!&raquo;. Желающие совершить доброе дело могут направляют свои средства в наш Фонд.</p>', '<p style=\"text-align: justify;\">&laquo;МЫҢ АЛҒЫС&raquo; қайырымдылық шарасы &laquo;Қазақстан&raquo; РТРК АҚ мен оның құрамына кіретін &laquo;QAZAQ RADIOLARY&raquo; ЖШС басшылықтарымен кездесу барысында қол жеткізілген уағдаластықтың нәтижесінде</p>\r\n\r\n<p style=\"text-align: justify;\">&laquo;МЫҢ АЛҒЫС&raquo; атты қайырымдылық шарасының аясында 2019 жылғы 11-28 ақпан аралығында өтіп жатқан &laquo;Қайырымдылықтан қалыс қалма! Алғысқа бөлен!&raquo; радио-видеомарафонына қатысуға шақырамыз.</p>', '<p style=\"text-align: justify;\">The charitable action of the partnership &laquo;Qazaq Radiolary&raquo;, named &laquo;Myn algys&raquo; (thousands of gratitudes) is passing from 11<sup>th</sup>&nbsp;to 28<sup>th</sup>&nbsp;of February. Within the action the radio stations &laquo;Shalqar&raquo;, &laquo;Astana&raquo;, &laquo;Classic&raquo; and &laquo;Qazaq radiosy&raquo; will invite famous public figures, artistic aesthetes, maecenas and promote the charity. The funds raised will be handed over to the needy people during the concert &laquo;Myn algys!&raquo;. People willing to to a good deed can allocate funds to our Fund.</p>', 1, 0, 1, '2019-02-11 07:49:00', '2019-05-23 06:42:35'),
(60, 'Встреча с руководством Ассоциации благотворительныx учреждений «Жібек жолы»', '«Жібек жолы» қайырымдылық мекемелерқауымдастығының басшылығымен кездесу', 'A meeting with the management of the Charitable organizations Association «Zhibek zholy»', 'images/uploads/082e8e8701d70aa129974aeb850e39cd.jpg', 'В xоде встречи стороны обсудили планы о совместной работе.', 'Кездесу барысында тараптар бірлескен жұмыс жоспарын талқылады.', 'During the meeting, the sides discussed the joint work plan.', '<p style=\"text-align: justify;\">Состоялась встреча с руководством Ассоциации благотворительныx учреждений &laquo;Жібек жолы&raquo;. В xоде встречи стороны обсудили планы о совместной работе.</p>', '<p>Қор қызметкерлері &laquo;Жібек жолы&raquo; қайырымдылық мекемелер қауымдастығының басшылығымен кездесті. Кездесу барысында тараптар бірлескен жұмыс жоспарын талқылады.</p>', '<p style=\"text-align: justify;\">A meeting was held between Fund&rsquo;s officials and the management of the Charitable organizations Association &laquo;Zhibek zholy&raquo;. During the meeting, the sides discussed the joint work plan.</p>', 1, 0, 1, '2019-02-18 07:52:00', '2019-05-11 13:53:07'),
(61, 'Встреча с Верховным муфтием Узбекистана', 'Өзбекстан Республикасының Бас мүфтиімен кездесу', 'Meeting with Usmonkhon hadji Alimov', 'images/uploads/a228144013ca4f59c937b2119a4d5b1e.jpg', 'В ходе переговоров К.Азимов ознакомил У.Алимова с деятельностью Фонда.', 'Аталмыш кездесуде Қ.Әзімов У.Алимовті Қор қызметімен таныстырды.', 'The management of the fund briefed Usmonkhon hadji Alimov on the work of the fund during the negotiations.', '<p style=\"text-align: justify;\">25 февраля с.г. в Ташкенте состоялась встреча с Председателем Духовного управления мусульман Узбекистана, Верховным муфтием, Усмонхон кажы Алимовым.</p>\r\n\r\n<p style=\"text-align: justify;\">В ходе переговоров К.Азимов ознакомил У.Алимова с деятельностью Фонда.</p>\r\n\r\n<p style=\"text-align: justify;\">В свою очередь, У.Алимов&nbsp;передал слова приветствия и наилучшие пожелания Председателю духовного управления мусульман Казахстана, Верховному муфтию Серикбаю кажы Ораз.</p>', '<p style=\"text-align: justify;\">Ү.ж. 25 ақпанда Ташкент қ. Қор президенті Қ.Әзімов Өзбекстан мұсылмандары діни басқармасының Төрағасы, Бас мүфти Усмонxон Алимовпен кездесті. Аталмыш кездесуде Қ.Әзімов У.Алимовті Қор қызметімен таныстырды. У.Алимов, өз кезегінде, Қазақстан мұсылмандары діни басқармасының (ҚМДБ) Төрағасы, Бас мүфти Серікбай қажы Оразға сәлемін жолдады.</p>', '<p style=\"text-align: justify;\">On 25 february 2019 the fund&rsquo;s President Kayrat Azimov met with Usmonkhon hadji Alimov, the Chairman of the Spiritual Administration of Muslims of Uzbekistan, the Grand Mufti.</p>\r\n\r\n<p style=\"text-align: justify;\">The management of the fund briefed Usmonkhon hadji Alimov on the work of the fund during the negotiations.</p>\r\n\r\n<p style=\"text-align: justify;\">Usmonkhon hadji Alimov, in turn, requested to convey greetings and best wishes to Serikbay hadji Oraz, the Chairman of the Spiritual Administration of Muslims of Kazakhstan, the Grand Mufti.</p>', 1, 0, 1, '2019-02-25 07:53:00', '2019-05-22 14:14:43'),
(62, 'На встрече с руководством Узбекистанского фонда «Вакф»', 'Қайрат Әзімов Өзбекстандық \"VAQF\" қайырымдылық қорының басшылығымен кездесу', 'Kayrat Azimov meeting with the management of «Vaqf» the Uzbekistan charitable fund', 'images/uploads/b48fa316e0b29f11b77360fbecd1dc2d.jpg', 'Во время состоявшейся встречи стороны обменялись опытом, достигли взаимопонимания и обсудили перспективы развития  отношений.', 'Келіссөздер шеңберінде тараптар екіжақты ынтымақтастықты талқылап, тәжірибе алмасты.', 'During the meeting the sides shared their experience and there had been a discussion on issues pertaining to the prospects for the promotion of bilateral cooperation.', '<p style=\"text-align: justify;\">25-26 февраля с.г. в Ташкенте состоялся официальный визит К.Азимова с руководством Узбекистанского фонда &laquo;Вакф&raquo;. Во время состоявшейся встречи стороны обменялись опытом, достигли взаимопонимания и обсудили перспективы развития&nbsp; отношений.</p>', '<article>\r\n<p style=\"text-align: justify;\">Ү.ж. 25-26 ақпанда қор президенті Қайрат Әзімов Ташкент қаласына (Өзбекстан) сапармен барды. Сапар барысында өзбекстандық &quot;VAQF&quot; қайырымдылық қорының басшылығымен және ұжымымен кездесті. Келіссөздер шеңберінде тараптар екіжақты ынтымақтастықты талқылап, тәжірибе алмасты.</p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n</article>', '<p style=\"text-align: justify;\">On 25-26 february 2019 the fund&rsquo;s President Kayrat Azimov met with the management of the Vaqf fund of the Republic of Uzbekistan.&nbsp;During the meeting the sides shared their experience and there had been a discussion on issues pertaining to the prospects for the promotion of bilateral cooperation.</p>', 1, 0, 1, '2019-02-25 07:56:00', '2019-05-11 13:57:15'),
(63, 'Благотворительная акция \"Мың алғыс\"', '\"Мың алғыс\" қайырымдылық акциясы', '\"Myn algys\" the charitable action', 'images/uploads/2875d3f6e295c2bae93004ead9ca3fa1.jpg', 'На мероприятии были подведены итоги благотворительной акции (радио-марафона) «Қайырымдылықтан қалыс қалма! Алғысқа бөлен!»', 'Аталмыш іс-шарада ү.ж. 11-28 ақпан аралығында өткен «Қайырымдылықтан қалыс қалма! Алғысқа бөлен!»', 'The donations were paid to the Fund’s account.', '<p style=\"text-align: justify;\">1 марта, в День Благодарности состоялся благотворительный концерт &laquo;Мың алғыс&raquo; во Дворце мира и согласия, организованный ТОО &laquo;Qazaq radiolary&raquo;. На мероприятии были подведены итоги благотворительной акции (радио-марафона) &laquo;Қайырымдылықтан қалыс қалма! Алғысқа бөлен!&raquo;, проходившей в период с 11 по 28 февраля с.г. Сбор пожертвований осуществлялся при помощи счета Фонда.</p>', '<p style=\"text-align: justify;\">1 наурыз - Алғыс айту күнінде Бейбітшілік пен келісім сарайында &laquo;Qazaq radiolary&raquo; ЖШС-мен ұйымдастырылған &laquo;Мың алғыс&raquo; қайырымдылық концерті өтті. Аталмыш іс-шарада ү.ж. 11-28 ақпан аралығында өткен &laquo;Қайырымдылықтан қалыс қалма! Алғысқа бөлен!&raquo; атты қайырымдылық шараның қорытындылары айтылды. Қол ұшын созған адамдар Қордың есеп-шотына қаражат бөлді.</p>', '<p style=\"text-align: justify;\">On 1 March, on Gratitude Day a charity concert was held in the Palace of Peace and Reconciliation, organized by Qazaq Radiolary LTD. The concert took stock the charitable action (radio marathon) known as &laquo;Qairymdylyqtan qalys kalma! Algyska b&oacute;len!&raquo; (Don&rsquo;t miss the charity! Bless you!), which was held during 11 to 28th February. The donations were paid to the Fund&rsquo;s account.</p>', 1, 0, 1, '2019-03-01 07:57:00', '2019-05-11 13:59:12'),
(64, 'Конференция по исламскому финансированию', 'Ислам қаржыландыру бойынша конференция', 'Conference on Islamic finance', 'images/uploads/da9aa67148c8d88251634540e33dbb30.jpg', '5 марта с.г. состоялась конференция по исламскому финансировснию в МФЦА.', 'Ү.ж. 5 наурызда Астана Халықаралық Қаржы Орталығында ислам каржыландыру тақырыбына қатысты конференция өтті.', 'On 5 March a conference on Islamic Finance was held in AIFC.', '<p style=\"text-align: justify;\">5 марта с.г. состоялась конференция по исламскому финансировснию в МФЦА.<br />\r\nДанное мероприятие было совместно организовано малайзийскими компаниями Amanie Advisors Sdn Bhd и CSQlaw.&nbsp;<br />\r\nЦелью мероприятия было всестороннее обсуждение на тему исламского финансирования, перспективы и трудности в Казаxстане.<br />\r\nНа конференции Посол Малайзии в РК Саид Моxамад Бакри Саид Абд Раxман выразил признание и поддержку Малайзии работе МФЦА по развитию исламского финансирования в Казаxстане.<br />\r\nСо стороны Фонда также будет вестись работа по развитию направления исламского финансирования в Казахстане. Waqyp management, дочерняя компания Фонда, зарегистрированная в юрисдикции МФЦА планирует привлекать инвестиции от учреждений исламского финансирования для реализации социально ориентированных проектов Фонда.</p>', '<p style=\"text-align: justify;\">Ү.ж. 5 наурызда Астана Халықаралық Қаржы Орталығында ислам каржыландыру тақырыбына қатысты конференция өтті.<br />\r\nАталмыш шараны екі малайзиялық компаниялар &laquo;Amanie International Consulting&raquo; және The Chambers of Shamsul Qamar ұйымдастырды.&nbsp;<br />\r\nМақсаты Ислам қаржыландыру және оның Қазақстандағы перспективалары мен қиындықтарын жан-жақты талқылау болды.<br />\r\nКонференцияда Малайзия мемлекетінің ҚР-дағы Төтенше және Өкілетті Елшісі Саид Моxамад Бакри Саид Абд Раxман сөз сөйлеп, АХҚО-ның Қазақстанда ислам қаржыландыру жүйесін дамыту қызметіне қолдау білдірді.<br />\r\nАХҚО юрисдикциясының астындағы Қордың еншілес компаниясы Waqyp Management әлеуметтік сипаттағы проекттерді жүзеге асыру мақсатында ислам қаржыландыру мекемелерден инвестицияларды тарту жұмыстарын жүргізеді.</p>', '<p style=\"text-align: justify;\">On 5 March a conference on Islamic Finance was held in AIFC.<br />\r\nThe event was jointly organized by two Malaysian firms namely Amanie Advisors Sdn Bhd and CSQlaw.&nbsp;<br />\r\nThe aim of the Conference was to provide comprehensive insights and discussions on Islamic Finance in Kazakhstan, its prospects and challenges.<br />\r\nThe Syed Mohamad Bakri, the Malaysian ambassador to Kazakhstan expressed Malaysian&rsquo;s recognition and support on commitment and seriousness of AIFC in promoting Islamic finance in Kazakhstan.<br />\r\nThe Waqyp Management, the subsidiary company of the Fund, working under jurisdiction of the AIFC plans to develop the area of Islamic Finance in Kazakhstan, attracting investments from Islamic Finance institutions in order to realize socially oriented projects of the Fund.</p>', 1, 0, 1, '2019-03-05 07:59:00', '2019-05-11 14:00:50'),
(65, 'Встреча с Малазийской консалтинговой компанией «Amanie International Consulting»', 'Amanie International Consulting компания өкілдерімен кездесу', 'A meeting with the representatives of the Amanie International Consulting', 'images/uploads/354394b69bdf6487827c290b5ecf4aac.jpg', '6 марта с.г. в г.Алматы состоялась встреча с представителями Малазийской консалтинговой компанией «Amanie International Consulting»', 'Ү.ж. 6 наурызда Алматы қ. Amanie International Consulting малайзиялық консалтингтік компания өкілдері Мохамад Дауд Бакар мырза', 'On 6 March a meeting with the representatives of the Amanie International Consulting, Malaysian consulting company was held in Almaty.', '<p style=\"text-align: justify;\">6 марта с.г. в г.Алматы состоялась встреча&nbsp;с представителями Малазийской консалтинговой компанией &laquo;Amanie International Consulting&raquo;, с господином Датук доктором Мохамад Дауд Бакаром, госпожой Сухайда Махрот, и господином Мохамад Ханиф, Мохамад Однаном. В ходе встречи менеджер отдела инвестиций и развития проектов Фонда Балабеков Айбек ознакомил с деятельностью и проектами Фонда. В свою очередь, малазийская сторона выразила готовность быть одними из инвесторов Фонда.</p>', '<p style=\"text-align: justify;\">Ү.ж. 6 наурызда Алматы қ. Amanie International Consulting малайзиялық консалтингтік компания өкілдері Мохамад Дауд Бакар мырза, Сухайда Махрот ханым, Мохамад Ханиф мырза және Мохамад Однаном мырзамен кездесу өтті. Кездесу барысында Қордың инвестициялар мен проекттер даму бөлімінің менеджері Балабеков Айбек Қор қызметі мен проекттерімен таныстырды. Малайзиялық тарап, өз кезегінде, Қор инвесторларының бірі болуға әзір екенін айтты.</p>', '<article>\r\n<p style=\"text-align: justify;\">On 6 March a meeting with the representatives of the Amanie International Consulting, Malaysian consulting company was held in Almaty. Issues on the Fund&rsquo;s activity and social projects were considered in the meeting. The Malaysian side, in turn, expressed its support and readiness to be one of the investors of the Fund.</p>\r\n</article>', 1, 0, 1, '2019-03-06 08:02:00', '2019-05-11 14:03:34'),
(66, 'Встреча с руководством Фонда «Вакф» им.Азиза Махмуда Худаи', 'Махмуд Азиз Худаи «Вакф» Қоры басшылығымен кездесу', 'A meeting with the managent of the Vaqf fund named after Mahmud Aziz Hudaiyi', 'images/uploads/9efa7ee0566e99ad6435c6835b48ba20.jpg', '5 марта с.г. в рамкаx визита руководства Фонда в Турецкую Республику состоялась встреча с руководством Фонда «Вакф» им.Азиза Махмуда Худаи в г.Стамбуле.', 'Сапар барысында 430 жылдық тарихы бар Түркияның Махмуд Азиз Худаи «Вакф» Қоры басшылығымен кездесті. ', 'On 5 March 2019 the Fund’s President met with the managent of the Vaqf fund named after Mahmud Aziz Hudaiyi.', '<p style=\"text-align: justify;\">5 марта с.г. в рамкаx визита руководства Фонда в Турецкую Республику состоялась встреча с руководством Фонда &laquo;Вакф&raquo; им.Азиза Махмуда Худаи в г.Стамбуле.</p>', '<p style=\"text-align: justify;\">Ү.ж. 5 наурызда Қор президенті Қайрат Әзімов Түркия Республикасына сапар жасады. Сапар барысында 430 жылдық тарихы бар Түркияның Махмуд Азиз Худаи &laquo;Вакф&raquo; Қоры басшылығымен кездесті.&nbsp;</p>', '<p style=\"text-align: justify;\">On 5 March 2019 the Fund&rsquo;s President met with the managent of the Vaqf fund named after Mahmud Aziz Hudaiyi.</p>', 1, 0, 1, '2019-03-05 08:04:00', '2019-05-11 14:05:22'),
(67, 'Встреча с представителями казахского Вакф фонда г.Стамбул', '«Қазақ түркілері УАҚФ» қоры өкілдерімен кездесу', 'A meeting with the Kazakh Waqf Fund\'s representatives', 'images/uploads/d81bffadf3d04b69144b0ca216161184.jpg', 'В рамках визита в Турецкую Республику президент Фонда Кайрат Азимов посетил Казахский Вакф фонд в г.Стамбуле.', 'Қор президенті Қайрат Әзімовтың Түркия Республикасына сапар барысында 6 наурызда «Қазақ түркілері УАҚФы» қорының қызметкерлерімен кездесті.', 'During the visit of the Fund’s President to Turkey a meeting was held with the Kazakh Waqf Fund in Istanbul.', '<p style=\"text-align: justify;\">В рамках визита в Турецкую Республику президент Фонда Кайрат Азимов посетил Казахский Вакф фонд в г.Стамбуле.<br />\r\nСправочно: Казахский Вакф фонд был создан 1986 году в г.Стамбуле по инициативе казахской диаспоры. Данный фонд занимается деятельностью по сохранению казахской культуры, традиции и истории среди казахской диаспоры. Также, у Фонда имеется специальный центр по подготовке чтению Священного Корана.</p>', '<article>\r\n<p style=\"text-align: justify;\">Қор президенті Қайрат Әзімовтың Түркия Республикасына сапар барысында 6 наурызда &laquo;Қазақ түркілері УАҚФы&raquo; қорының қызметкерлерімен кездесті.<br />\r\nАнықтама: &laquo;Қазақ түркілері УАҚФ&raquo; қоры 1986 ж. Стамбул қ. қазақ зиялылары мен диаспора өкілдерімен құрылды. Аталмыш қор қазақ дәстүрін, мәдениетін, тариxын насиxаттау жұмыстарын жүргізеді. Сонымен қатар, қасиетті Құран жаттау арнайы орталығы бар.</p>\r\n</article>', '<p style=\"text-align: justify;\">During the visit of the Fund&rsquo;s President to Turkey a meeting was held with the Kazakh Waqf Fund in Istanbul.<br />\r\nReference: Kazakh Waqf Fund was founded in 1986 in Istanbul at the initiative of the Kazakh diaspora. The Fund&rsquo;s activity is aimed at the preservation of Kazakh culture, traditions and history among the Kazakh diaspora in Turkey. In addition, the Fund has a Quran Training Center.</p>', 1, 0, 1, '2019-03-06 08:05:00', '2019-05-11 14:06:55'),
(70, '5-6 желтоқсанда Малака қаласында (Малайзия) Дүниежүзілік Зекет форумының 7-ші халықаралық конференциясы өтті.', '5-6 желтоқсанда Малака қаласында (Малайзия) Дүниежүзілік Зекет форумының 7-ші халықаралық конференциясы өтті.', NULL, 'images/uploads/d0f82632b689ef360a8c1db9970c3dbc.jpg', '- Ү.ж. 5-6 желтоқсанда Малака қаласында (Малайзия) Дүниежүзілік Зекет форумының 7-ші халықаралық конференциясы өтті. Іс-шараның басты тақырыбы - Мұсылман үмметінің әл-ауқатын арттырудағы Зекеттің жаһандық ынтымақтастығын күшейту. Конференция жұмысына 70 жуық елден 300 астам делегат қатысты.', NULL, NULL, '<p>- Ү.ж. 5-6 желтоқсанда Малака қаласында (Малайзия) Дүниежүзілік Зекет форумының 7-ші халықаралық конференциясы өтті. Іс-шараның басты тақырыбы - Мұсылман үмметінің әл-ауқатын арттырудағы Зекеттің жаһандық ынтымақтастығын күшейту. Конференция жұмысына 70 жуық елден 300 астам делегат қатысты.</p>\r\n\r\n<p>Оның шеңберінде &quot;Зекет және орнықты дамудың мақсаттары&quot; тақырыбында баяндама жасалып, &laquo;УАҚЫП&raquo; Қазақстандық қайырымдылық қорының&raquo; презентациясы өткізілді.<br />\r\n<br />\r\nК.Азимов:&quot;Бұл бағыттағы/тақырыптағы шараға бірінші рет қатысқаннан кейін бе, аталмыш жиында көтерілген/талқыланған мәселелер, оның ішінде әлем мұсылмандарының жалпы мұқтаждарға деген көңілі мен көмегіне, мейірімі мен қайырымдылығына риза болдым&quot;.</p>', '<p>- Ү.ж. 5-6 желтоқсанда Малака қаласында (Малайзия) Дүниежүзілік Зекет форумының 7-ші халықаралық конференциясы өтті. Іс-шараның басты тақырыбы - Мұсылман үмметінің әл-ауқатын арттырудағы Зекеттің жаһандық ынтымақтастығын күшейту. Конференция жұмысына 70 жуық елден 300 астам делегат қатысты.</p>\r\n\r\n<p>Оның шеңберінде &quot;Зекет және орнықты дамудың мақсаттары&quot; тақырыбында баяндама жасалып, &laquo;УАҚЫП&raquo; Қазақстандық қайырымдылық қорының&raquo; презентациясы өткізілді.<br />\r\n<br />\r\nК.Азимов:&quot;Бұл бағыттағы/тақырыптағы шараға бірінші рет қатысқаннан кейін бе, аталмыш жиында көтерілген/талқыланған мәселелер, оның ішінде әлем мұсылмандарының жалпы мұқтаждарға деген көңілі мен көмегіне, мейірімі мен қайырымдылығына риза болдым&quot;.</p>', '', 1, 1, 1, '2018-12-06 23:18:00', '2019-05-15 12:03:50'),
(71, 'Нурсултан Назарбаев написал письмо выпускникам НИШ 2058 года', 'Нурсултан Назарбаев написал письмо выпускникам НИШ 2058 года', NULL, 'images/uploads/2acf963bd4ae567c53713cbea5c720b3.jpg', 'Назарбаев Интеллектуальным школам вручили капсулу времени с письмом Президента РК, передаёт корреспондент BNews.kz. Глава государства вручил обращение к будущим выпускникам НИШ на прошедшем в Астане форуме \"Ұлы дала мұрагерлері\". Ознакомиться с содержанием письма смогут только выпускники 2058 года. За это время, по мнению Елбасы, в Казахстане всё будет по-другому.', 'Назарбаев Интеллектуальным школам вручили капсулу времени с письмом Президента РК, передаёт корреспондент BNews.kz. Глава государства вручил обращение к будущим выпускникам НИШ на прошедшем в Астане форуме \"Ұлы дала мұрагерлері\". Ознакомиться с содержанием письма смогут только выпускники 2058 года. За это время, по мнению Елбасы, в Казахстане всё будет по-другому.\r\n\r\n\"Это письмо предназначено для выпускников 2058 года. Есть такое высказывание в народе: \"Елу жылда ел жаңа\". В это время вы будете молодыми. В это время будут совершенно другие новые технологии. Вы откроете и прочитаете это письмо\", - сказал Нурсултан Назарбаев.\r\n\r\nВ ходе форума Главе государства также вручили знак почётного члена ассоциации выпускников НИШ.', NULL, '<p>Назарбаев Интеллектуальным школам вручили&nbsp;капсулу времени с письмом Президента РК, передаёт корреспондент&nbsp;BNews.kz. Глава государства вручил обращение к будущим выпускникам НИШ на прошедшем в Астане форуме&nbsp;&quot;Ұлы дала мұрагерлері&quot;. Ознакомиться с содержанием письма смогут только выпускники 2058 года. За это время, по мнению Елбасы, в Казахстане всё будет по-другому.</p>\r\n\r\n<p>&quot;Это письмо предназначено для&nbsp;выпускников 2058 года. Есть такое высказывание в народе: &quot;Елу жылда ел жаңа&quot;.&nbsp;В это время вы будете молодыми. В это время будут совершенно другие новые технологии. Вы откроете и прочитаете это письмо&quot;, - сказал Нурсултан Назарбаев.</p>\r\n\r\n<p>В ходе форума Главе государства также вручили знак почётного члена ассоциации выпускников НИШ.</p>', '<p>Назарбаев Интеллектуальным школам вручили&nbsp;капсулу времени с письмом Президента РК, передаёт корреспондент&nbsp;BNews.kz. Глава государства вручил обращение к будущим выпускникам НИШ на прошедшем в Астане форуме&nbsp;&quot;Ұлы дала мұрагерлері&quot;. Ознакомиться с содержанием письма смогут только выпускники 2058 года. За это время, по мнению Елбасы, в Казахстане всё будет по-другому.</p>\r\n\r\n<p>&quot;Это письмо предназначено для&nbsp;выпускников 2058 года. Есть такое высказывание в народе: &quot;Елу жылда ел жаңа&quot;.&nbsp;В это время вы будете молодыми. В это время будут совершенно другие новые технологии. Вы откроете и прочитаете это письмо&quot;, - сказал Нурсултан Назарбаев.</p>\r\n\r\n<p>В ходе форума Главе государства также вручили знак почётного члена ассоциации выпускников НИШ.</p>', '', 1, 1, 1, '2018-12-05 05:26:00', '2019-05-15 12:04:32'),
(72, 'Более полутора миллионам человек были прочтены проповеди в мечетях столицы', NULL, NULL, 'images/uploads/98364488a8a687511ff176168b58bd47.jpg', 'В 2018 году ежедневно в мечетях столицы после  полуденного и послеполуденного намазов читались проповеди на темы исламской истории, веры, тафсира, хадисов, жизни пророков, истории из Корана, этики, основ Ислама и т.д.', NULL, NULL, '<p>В 2018 году ежедневно в мечетях столицы после&nbsp; полуденного и послеполуденного намазов читались проповеди на темы исламской истории, веры, тафсира, хадисов, жизни пророков, истории из Корана, этики, основ Ислама и т.д.</p>\r\n\r\n<p>По словам заместителя главного имама Астаны Бакытжана Уткелбаева, в кампании приняли участие 502 410 человек. Кроме того, в течение года в столице религиозное образование получили 3 672 человек. Устазы столичной мечети провели 513 встреч с представителями нетрадиционных религиозных течений. В результате 20 человек вернулись в&nbsp; традиционный Ислам.</p>\r\n\r\n<p>По результатам благотворительной работы в 2018 году мечетью была оказана помощь 8 497 семьям. Более 20 тонн жертвенного мяса были распределены среди 2 981 семей в Курбан айт , а 1 270 шакиртов были обеспечены необходимыми учебниками в рамках акции &laquo;Дорога в школу&raquo;. Стоит отметить, что в Астане есть 9 мечетей.</p>', '', '', 1, 1, 1, '2018-12-05 05:33:00', '2019-05-15 12:05:35'),
(73, 'За год в Алматы была оказана помощь 164 348 семьям', 'За год в Алматы была оказана помощь 164 348 семьям', NULL, 'images/uploads/207112c8c04bf0aeea88ba4ac7b6ab6e.jpg', 'В Алматы под председательством Верховного муфтия Серикбая кажы Ораза прошло заседание Президиума по итоговому годовому отчету.', 'В Алматы под председательством Верховного муфтия Серикбая кажы Ораза прошло заседание Президиума по итоговому годовому отчету.\r\n\r\nГлавный имам Алматы Еркинбек Шокай отметил, что в отчетном году имамы города провели 1025 благотворительных мероприятий, и была оказана помощь 164 348 малообеспеченным семьям и нуждающимся гражданам.\r\n\r\nКроме того, более 15 тонн предметов домашнего обихода были доставлены жителям Восточно-Казахстанской области, пострадавшим от наводнений.\r\n\r\nВ течение года религиозное образование в алматинских мечетях получили 7822 человек. Стоит отметить, что в Алматы функционируют 50 мечетей и работают 221 священнослужителей.', NULL, '<p dir=\"auto\">В Алматы под председательством Верховного муфтия Серикбая кажы Ораза прошло заседание Президиума по итоговому годовому отчету.</p>\r\n\r\n<p dir=\"auto\">Главный имам Алматы Еркинбек Шокай отметил, что в отчетном году имамы города провели 1025 благотворительных мероприятий, и была оказана помощь 164 348 малообеспеченным семьям и нуждающимся гражданам.</p>\r\n\r\n<p dir=\"auto\">Кроме того, более 15 тонн предметов домашнего обихода были доставлены жителям Восточно-Казахстанской области, пострадавшим от наводнений.</p>\r\n\r\n<p dir=\"auto\">В течение года религиозное образование в алматинских мечетях получили 7822 человек. Стоит отметить, что в Алматы функционируют 50 мечетей и работают 221 священнослужителей.&nbsp;&nbsp;</p>', '<p dir=\"auto\">В Алматы под председательством Верховного муфтия Серикбая кажы Ораза прошло заседание Президиума по итоговому годовому отчету.</p>\r\n\r\n<p dir=\"auto\">Главный имам Алматы Еркинбек Шокай отметил, что в отчетном году имамы города провели 1025 благотворительных мероприятий, и была оказана помощь 164 348 малообеспеченным семьям и нуждающимся гражданам.</p>\r\n\r\n<p dir=\"auto\">Кроме того, более 15 тонн предметов домашнего обихода были доставлены жителям Восточно-Казахстанской области, пострадавшим от наводнений.</p>\r\n\r\n<p dir=\"auto\">В течение года религиозное образование в алматинских мечетях получили 7822 человек. Стоит отметить, что в Алматы функционируют 50 мечетей и работают 221 священнослужителей.&nbsp;&nbsp;</p>', '', 1, 1, 1, '2018-12-05 05:38:00', '2019-05-15 12:05:46'),
(74, 'Состоялся Республиканский семинар практикум на тему «Светское Государство и религиозные отношения: межрегиональный обмен опытом»', NULL, NULL, 'images/uploads/3b81f7e7073911d407bf6185ee0a46b9.jpg', 'Управление по делам религий Алматинской области провел республиканский семинар-практикум на тему «Светское государство и религиозные отношения: межрегиональный обмен опытом», согласованный с Комитетом общественного согласия Министерства общественного развития Республики Казахстан.', NULL, NULL, '<p>Управление по делам религий Алматинской области провел республиканский семинар-практикум на тему&nbsp;&laquo;Светское государство и религиозные отношения: межрегиональный обмен опытом&raquo;,&nbsp;согласованный с Комитетом общественного согласия Министерства общественного развития Республики Казахстан.<br />\r\nЦель семинара&nbsp;реализация государственной политики в религиозной сфере, обеспечение стабильности религиозной ситуации в стране, продвижение светских принципов, обмен опытом в области изучения религиозной ситуации, обеспечение межконфессионального согласия и предотвращение негативных идей нетрадиционных религиозных движений.<br />\r\nВ работе семинара приняли участие:&nbsp;Вице-министр общественного развития Республики Казахстан&nbsp;Берик Арын Сакбаевич, Заведующий сектором Отдела внутренней политики Администрации Президента Республики Казахстан&nbsp;Шойкин Галым Нурмагамбетович,заместитель акима Алматинской области&nbsp;Абдуалиев Акан Жылкышыбаевич, проректор Египетского университета исламской культуры Нур-Мубарак&nbsp;Кайрат Курманбаев, Декан факультета востоковедения Казахского национального университета имени аль-Фараби, PhD, доктор философии&nbsp;Ыктияр Молдатореулы Палторе, преподаватель &laquo;Антитеррористического центра&raquo; КНБ РК, религиовед&nbsp;Шоманов Болат Шаймолданович, руководители областных управлении по делам религий и директора Центров, ректора ВУЗов, эксперты в области религии.<br />\r\nВ рамках семинара пройдут Фотовыставка&nbsp;о деятельности Управления по делам религий Алматинской области и показ фильма под названием&nbsp;&laquo;Дін &ndash; бірліктің рухани тірегі&raquo;. Далле, ознакомление с работой&nbsp;&laquo;Центра исследования проблем в сфере религий&raquo;&nbsp;и&nbsp;&laquo;Центра по реабилитации жертв религиозного экстремизма и приверженцев деструктивных культов&raquo;действующих при областном Управлении по делам религий.<br />\r\nВ Казахстане зарегистрировано более 3,5 тысяч религиозных субъектов&nbsp;18 различных конфессии. Несмотря на такое разнообразие, мы сумели сохранить равновесие обеспечения национальной безопасности и защиты свободы вероисповедания. Мы смогли сформировать в нашей стране уникальную общепризнанную казахстанскую модель межконфессионального мира и согласия. Уверен, что опыт Казахстана можно полностью использовать и в мировом масштабе.<br />\r\nН.А. Назарбаев<br />\r\n(выступление на VI Съезде лидеров<br />\r\nмировых и традиционных религий)</p>', '', '', 1, 1, 1, '2018-12-04 05:40:00', '2019-05-15 12:06:00');
INSERT INTO `news` (`id`, `title_ru`, `title_kk`, `title_en`, `img`, `sh_desc_ru`, `sh_desc_kk`, `sh_desc_en`, `desc_ru`, `desc_kk`, `desc_en`, `view`, `is_archive`, `status`, `created_at`, `updated_at`) VALUES
(75, 'В Алматы прошел духовный вечер Маулит', 'В Алматы прошел духовный вечер Маулит', NULL, 'images/uploads/7ad08fbb840b36ed1a0d26f8038eef25.jpg', 'Вчера во Дворце республики состоялся духовный вечер Маулит под названием «Тәлімгер пайғамбар тағылымы» инициированный Духовным управлением мусульман Казахстана.\r\n\r\nВечер Маулит начался с чтения Корана известного кари Серика Ахметова и исполнения песни(жыр) поэта, композитора Жакена Омара. Маулит в этом году отличился тем, что полностью был прочитан на казахском языке.', 'Вчера во Дворце республики состоялся духовный вечер Маулит под названием «Тәлімгер пайғамбар тағылымы» инициированный Духовным управлением мусульман Казахстана.\r\n\r\nВечер Маулит начался с чтения Корана известного кари Серика Ахметова и исполнения песни(жыр) поэта, композитора Жакена Омара. Маулит в этом году отличился тем, что полностью был прочитан на казахском языке. \r\n\r\nДля торжественного открытия праздничного мероприятия слово было дано председателю Духовного управления мусульман Казахстана, Верховному муфтию Серикбай кажы Оразу.', NULL, '<p>Вчера во Дворце республики состоялся духовный вечер Маулит&nbsp;под названием &laquo;Тәлімгер пайғамбар тағылымы&raquo; инициированный&nbsp;Духовным управлением мусульман Казахстана.</p>\r\n\r\n<p>Вечер Маулит начался с чтения Корана известного кари Серика Ахметова и исполнения песни(жыр) поэта, композитора Жакена Омара. Маулит в этом году отличился тем, что полностью был прочитан&nbsp;на казахском языке.&nbsp;</p>\r\n\r\n<p>Для торжественного открытия праздничного мероприятия слово было дано председателю Духовного управления мусульман Казахстана, Верховному муфтию Серикбай кажы Оразу. &nbsp;</p>\r\n\r\n<p>В своей речи Верховный муфтий поздравил алматинцев с месяцем Маулит, по его словам, появление на свет Посланника Аллаха &nbsp;и свет, которым он озарил весь мир - это большая радость для его последователей. Также, он поделился историями из жизни Пророка (да будет доволен им Аллах и приветствует), призвал мусульман брать в пример Его мягкий нрав. Также он остановился на наставничестве Пророка, как говорится в аяте&nbsp;&laquo;Мы отправили тебя только в качестве милости к мирам&raquo;.&nbsp;</p>\r\n\r\n<p>Уже полюбившиеся народу театры юмора &laquo;Өнер қырандары&raquo; и &laquo;Назар аудар&raquo; в очередной раз подарили зрителям спектакль с большим воспитательным смыслом и хорошим юмором.&nbsp;</p>\r\n\r\n<p>Прочитали свои стихи Кул-Керим Елемес, Бауыржан Мырзакул и Батырхан Сарсенхан, которые посвятили свои поэтические таланты на призвание народа к иману и благодеянию.&nbsp;</p>\r\n\r\n<p>На праздничном вечере Айдар Турганбек исполнил песню &laquo;Пайғамбарға (c.ғ.с) сағыныш&raquo;, а известные певцы как Ерген Айтымбетов, Канатбек Полатбек, Сержанали Алибек,&nbsp;Райым Уайыс спели песню салават с почтением к Пророку.&nbsp;</p>\r\n\r\n<p>Общественность Алматы, которая пришла на благотворный праздник Маулит, под сильным впечатлением, дала волю слезам. Не было меры в благосклонности зрителей, которые проявляли большую почесть.&nbsp;</p>\r\n\r\n<p>Пусть всем нам будет &nbsp;дарована милость Аллаха и&nbsp;заступничество Пророка Мухаммада (да благословит его Аллах и приветствует)!</p>', '<p>Вчера во Дворце республики состоялся духовный вечер Маулит&nbsp;под названием &laquo;Тәлімгер пайғамбар тағылымы&raquo; инициированный&nbsp;Духовным управлением мусульман Казахстана.</p>\r\n\r\n<p>Вечер Маулит начался с чтения Корана известного кари Серика Ахметова и исполнения песни(жыр) поэта, композитора Жакена Омара. Маулит в этом году отличился тем, что полностью был прочитан&nbsp;на казахском языке.&nbsp;</p>\r\n\r\n<p>Для торжественного открытия праздничного мероприятия слово было дано председателю Духовного управления мусульман Казахстана, Верховному муфтию Серикбай кажы Оразу. &nbsp;</p>\r\n\r\n<p>В своей речи Верховный муфтий поздравил алматинцев с месяцем Маулит, по его словам, появление на свет Посланника Аллаха &nbsp;и свет, которым он озарил весь мир - это большая радость для его последователей. Также, он поделился историями из жизни Пророка (да будет доволен им Аллах и приветствует), призвал мусульман брать в пример Его мягкий нрав. Также он остановился на наставничестве Пророка, как говорится в аяте&nbsp;&laquo;Мы отправили тебя только в качестве милости к мирам&raquo;.&nbsp;</p>\r\n\r\n<p>Уже полюбившиеся народу театры юмора &laquo;Өнер қырандары&raquo; и &laquo;Назар аудар&raquo; в очередной раз подарили зрителям спектакль с большим воспитательным смыслом и хорошим юмором.&nbsp;</p>\r\n\r\n<p>Прочитали свои стихи Кул-Керим Елемес, Бауыржан Мырзакул и Батырхан Сарсенхан, которые посвятили свои поэтические таланты на призвание народа к иману и благодеянию.&nbsp;</p>\r\n\r\n<p>На праздничном вечере Айдар Турганбек исполнил песню &laquo;Пайғамбарға (c.ғ.с) сағыныш&raquo;, а известные певцы как Ерген Айтымбетов, Канатбек Полатбек, Сержанали Алибек,&nbsp;Райым Уайыс спели песню салават с почтением к Пророку.&nbsp;</p>\r\n\r\n<p>Общественность Алматы, которая пришла на благотворный праздник Маулит, под сильным впечатлением, дала волю слезам. Не было меры в благосклонности зрителей, которые проявляли большую почесть.&nbsp;</p>\r\n\r\n<p>Пусть всем нам будет &nbsp;дарована милость Аллаха и&nbsp;заступничество Пророка Мухаммада (да благословит его Аллах и приветствует)!</p>', '', 1, 1, 1, '2018-11-27 23:42:00', '2019-05-15 12:06:13'),
(76, 'Военнослужащие Вооруженных Сил перечислили однодневный заработок в благотворительный Фонд «УAКЫП»', 'Военнослужащие Вооруженных Сил перечислили однодневный заработок в благотворительный Фонд «УAКЫП»', NULL, 'images/uploads/946b51c436e5d68ec1a1ca2d844ef71b.jpg', 'Руководство, военнослужащие центрального аппарата и управлений Главнокомандующих видами Вооруженных Сил Республики Казахстан на добровольной основе перечислили однодневную заработную плату на расчетный счет Корпоративного благотворительного Фонда «Казахстанский фонд «Уақып».', 'Руководство, военнослужащие центрального аппарата и управлений Главнокомандующих видами Вооруженных Сил Республики Казахстан на добровольной основе перечислили однодневную заработную плату на расчетный счет Корпоративного благотворительного Фонда «Казахстанский фонд «Уақып». Благотворительная деятельность Фонда направлена на оказание всемерной поддержки развитию традиционных духовно-нравственных ценностей, укреплению мира, дружбы и согласия в обществе, недопущению использования религии в деструктивных целях, поддержку и защиту граждан, оказанию материальной помощи (благотворительности) малообеспеченным, безработным, инвалидам и иным категориям социально незащищённых лиц.', NULL, '<p>Руководство, военнослужащие центрального аппарата и управлений Главнокомандующих видами Вооруженных Сил Республики Казахстан на добровольной основе перечислили однодневную заработную плату на расчетный счет Корпоративного благотворительного Фонда &laquo;Казахстанский фонд &laquo;Уақып&raquo;. Благотворительная деятельность Фонда направлена на оказание всемерной поддержки развитию традиционных духовно-нравственных ценностей, укреплению мира, дружбы и согласия в обществе, недопущению использования религии в деструктивных целях, поддержку и защиту граждан, оказанию материальной помощи (благотворительности) малообеспеченным, безработным, инвалидам и иным категориям социально незащищённых лиц.</p>', '<p>Руководство, военнослужащие центрального аппарата и управлений Главнокомандующих видами Вооруженных Сил Республики Казахстан на добровольной основе перечислили однодневную заработную плату на расчетный счет Корпоративного благотворительного Фонда &laquo;Казахстанский фонд &laquo;Уақып&raquo;. Благотворительная деятельность Фонда направлена на оказание всемерной поддержки развитию традиционных духовно-нравственных ценностей, укреплению мира, дружбы и согласия в обществе, недопущению использования религии в деструктивных целях, поддержку и защиту граждан, оказанию материальной помощи (благотворительности) малообеспеченным, безработным, инвалидам и иным категориям социально незащищённых лиц.</p>', '', 1, 1, 1, '2018-11-27 05:45:00', '2019-05-15 12:06:28'),
(77, 'Елорда имамдары жылдық қорытынды есеп берді', 'Елорда имамдары жылдық қорытынды есеп берді', NULL, 'images/uploads/3b954253fe138ef4f7171e483642dec2.jpg', 'Бүгін республикалық «Әзірет Сұлтан» мешітінде ҚМДБ төрағасының бірінші орынбасары, Астана қаласының өкіл имамы Наурызбай қажы Тағанұлының төрағалығымен Елорда имамдарының жылдық қорытынды есеп беру отырысы өтті. Алқалы жиында Астана қаласындағы тоғыз мешітте жыл бойы атқарылған шаралар есебі көрсетіліп, бас имамдар баяндама жасады.', 'Бүгін республикалық «Әзірет Сұлтан» мешітінде ҚМДБ төрағасының бірінші орынбасары, Астана қаласының өкіл имамы Наурызбай қажы Тағанұлының төрағалығымен Елорда имамдарының жылдық қорытынды есеп беру отырысы өтті. Алқалы жиында Астана қаласындағы тоғыз мешітте жыл бойы атқарылған шаралар есебі көрсетіліп, бас имамдар баяндама жасады. Сондай-ақ, «Астана» медресе колледжі мен «Зекет» қорының Астана өкілдігінің де жылдық есебі тыңдалды.\r\n\r\nЖаңа нұсқа бойынша өткен жылдық есеп беру жиналысында ҚМДБ тарапынан бекітілген оқу-ағарту, уағыз-насихат, қайырымдылық және теріс ағыммен күрес бағыттары бойынша жалпы мәлімет беріліп, фотосуреттермен нақтыланып отырды. Сонымен қатар, кейбір мәліметтер бойынша қосымша сұрақтар қойылып, мешіттегі түрлі мәселелердің шешу тетігі қарастырылды.\r\n\r\nЖиналыс барысында айтылған шаралар есебіне қатысты наиб мүфти Наурызбай қажы Тағанұлы жалпы оң бағасын беріп, дін жолындағы жұмыстарына сәттілік тіледі. Кейбір құлшылық ордаларындағы кемшіліктер жайы ескертіліп, келесі жылғы жоспарға енгізіу жайттары айтылды.', NULL, '<p>Бүгін республикалық &laquo;Әзірет Сұлтан&raquo; мешітінде ҚМДБ төрағасының бірінші орынбасары, Астана қаласының өкіл имамы Наурызбай қажы Тағанұлының төрағалығымен Елорда имамдарының жылдық қорытынды есеп беру отырысы өтті. Алқалы жиында Астана қаласындағы тоғыз мешітте жыл бойы атқарылған шаралар есебі көрсетіліп, бас имамдар баяндама жасады. Сондай-ақ, &laquo;Астана&raquo; медресе колледжі мен &laquo;Зекет&raquo; қорының Астана өкілдігінің де жылдық есебі тыңдалды.</p>\r\n\r\n<p>Жаңа нұсқа бойынша өткен жылдық есеп беру жиналысында ҚМДБ тарапынан бекітілген оқу-ағарту, уағыз-насихат, қайырымдылық және теріс ағыммен күрес бағыттары бойынша жалпы мәлімет беріліп, фотосуреттермен нақтыланып отырды. Сонымен қатар, кейбір мәліметтер бойынша қосымша сұрақтар қойылып, мешіттегі түрлі мәселелердің шешу тетігі қарастырылды.</p>\r\n\r\n<p>Жиналыс барысында айтылған шаралар есебіне қатысты наиб мүфти Наурызбай қажы Тағанұлы жалпы оң бағасын беріп, дін жолындағы жұмыстарына сәттілік тіледі. Кейбір құлшылық ордаларындағы кемшіліктер жайы ескертіліп, келесі жылғы жоспарға енгізіу жайттары айтылды.</p>', '<p>Бүгін республикалық &laquo;Әзірет Сұлтан&raquo; мешітінде ҚМДБ төрағасының бірінші орынбасары, Астана қаласының өкіл имамы Наурызбай қажы Тағанұлының төрағалығымен Елорда имамдарының жылдық қорытынды есеп беру отырысы өтті. Алқалы жиында Астана қаласындағы тоғыз мешітте жыл бойы атқарылған шаралар есебі көрсетіліп, бас имамдар баяндама жасады. Сондай-ақ, &laquo;Астана&raquo; медресе колледжі мен &laquo;Зекет&raquo; қорының Астана өкілдігінің де жылдық есебі тыңдалды.</p>\r\n\r\n<p>Жаңа нұсқа бойынша өткен жылдық есеп беру жиналысында ҚМДБ тарапынан бекітілген оқу-ағарту, уағыз-насихат, қайырымдылық және теріс ағыммен күрес бағыттары бойынша жалпы мәлімет беріліп, фотосуреттермен нақтыланып отырды. Сонымен қатар, кейбір мәліметтер бойынша қосымша сұрақтар қойылып, мешіттегі түрлі мәселелердің шешу тетігі қарастырылды.</p>\r\n\r\n<p>Жиналыс барысында айтылған шаралар есебіне қатысты наиб мүфти Наурызбай қажы Тағанұлы жалпы оң бағасын беріп, дін жолындағы жұмыстарына сәттілік тіледі. Кейбір құлшылық ордаларындағы кемшіліктер жайы ескертіліп, келесі жылғы жоспарға енгізіу жайттары айтылды.</p>', '', 1, 1, 1, '2018-11-27 23:46:00', '2019-05-15 12:05:26'),
(78, 'ВЕРХОВНЫЙ МУФТИЙ ПРИНЯЛ УЧАСТИЕ В ОТКРЫТИИ НОВОЙ МЕЧЕТИ В ТАРАЗЕ', 'ВЕРХОВНЫЙ МУФТИЙ ПРИНЯЛ УЧАСТИЕ В ОТКРЫТИИ НОВОЙ МЕЧЕТИ В ТАРАЗЕ', NULL, 'images/uploads/0d8f3e13c776b2978b01b1e7d129ee83.jpg', 'Сегодня новая мечеть в Таразе открыла свои двери для джамаата.Председатель Духовного управления мусульман Казахстана Серикбай кажы Ораз принял участие в церемонии открытия Дома Аллаха.Выступая на церемонии, Верховный муфтий Серикбай кажы Сатыбалдыулы сказал:', 'Сегодня новая мечеть в Таразе открыла свои двери для джамаата.Председатель Духовного управления мусульман Казахстана Серикбай кажы Ораз принял участие в церемонии открытия Дома Аллаха.Выступая на церемонии, Верховный муфтий Серикбай кажы Сатыбалдыулы сказал:\r\n\r\n\"Аллах возвеличит степень построившего мечеть в обоих мирах. \"Пророк (с.а.с.) сказал: «Тому, кто построит мечеть, Аллах построит дом в Раю» (Бухари). Строительство мечети - это благое дело. Хвала Аллаху, число мечетей с каждым годом увеличивается. Я хотел бы поблагодарить тех, кто принял участие в строительстве этого храма.  Я убежден, что дом поклонения станет местом благочестия, единства и солидарности».\r\nПо завершении торжественного мероприятия председатель Духовного управления мусульман Казахстана подарил священную книгу Коран имаму мечети «Наметбай».\r\n\r\nСтоит отметить, что в новой мечети одновременно могут молиться 800 человек.', NULL, '<p>Сегодня новая мечеть в Таразе открыла свои двери для джамаата.Председатель Духовного управления мусульман Казахстана Серикбай кажы Ораз принял участие в церемонии открытия Дома Аллаха.Выступая на церемонии, Верховный муфтий Серикбай кажы Сатыбалдыулы сказал:</p>\r\n\r\n<p>&quot;Аллах возвеличит степень построившего мечеть в обоих мирах.&nbsp;&quot;Пророк (с.а.с.) сказал: &laquo;Тому, кто построит мечеть, Аллах построит дом в Раю&raquo; (Бухари). Строительство мечети - это благое дело. Хвала Аллаху, число мечетей с каждым годом увеличивается. Я хотел бы поблагодарить тех, кто принял участие в строительстве этого храма.&nbsp; Я убежден, что дом поклонения станет местом благочестия, единства и солидарности&raquo;.</p>\r\n\r\n<p>По завершении торжественного мероприятия председатель Духовного управления мусульман Казахстана подарил священную книгу Коран имаму мечети &laquo;Наметбай&raquo;.</p>\r\n\r\n<p>Стоит отметить, что в новой мечети одновременно могут молиться 800 человек.</p>', '<p>Сегодня новая мечеть в Таразе открыла свои двери для джамаата.Председатель Духовного управления мусульман Казахстана Серикбай кажы Ораз принял участие в церемонии открытия Дома Аллаха.Выступая на церемонии, Верховный муфтий Серикбай кажы Сатыбалдыулы сказал:</p>\r\n\r\n<p>&quot;Аллах возвеличит степень построившего мечеть в обоих мирах.&nbsp;&quot;Пророк (с.а.с.) сказал: &laquo;Тому, кто построит мечеть, Аллах построит дом в Раю&raquo; (Бухари). Строительство мечети - это благое дело. Хвала Аллаху, число мечетей с каждым годом увеличивается. Я хотел бы поблагодарить тех, кто принял участие в строительстве этого храма.&nbsp; Я убежден, что дом поклонения станет местом благочестия, единства и солидарности&raquo;.</p>\r\n\r\n<p>По завершении торжественного мероприятия председатель Духовного управления мусульман Казахстана подарил священную книгу Коран имаму мечети &laquo;Наметбай&raquo;.</p>\r\n\r\n<p>Стоит отметить, что в новой мечети одновременно могут молиться 800 человек.</p>', '', 1, 1, 1, '2018-11-27 05:49:00', '2019-05-15 11:51:32'),
(79, 'ОТДЕЛ ХАЛАЛ-СТАНДАРТИЗАЦИИ ДУМК ВОШЕЛ В СПИСОК JAKIM', 'ОТДЕЛ ХАЛАЛ-СТАНДАРТИЗАЦИИ ДУМК ВОШЕЛ В СПИСОК JAKIM', NULL, 'images/uploads/9d567dacfc83e1ca62f95b8dd5e1f8e4.jpg', 'ТОО «Халал Даму» при Духовном управлении мусульман Казахстана, которое занимается халал стандартизацией и сертификацией продукции, аккредитовано Малазийским департаментом исламского развития (JAKIM). В результате, он включен в список 75 компаний, занимающихся халал стандартизацией, в которые входят 43 государства мира. К ним относятся США, Великобритания, Китай, Германия, Франция, Италия, Корея, Япония и мусульманские страны, такие как Египет, Пакистан, ОАЭ.', NULL, NULL, '<p>ТОО &laquo;Халал Даму&raquo; при Духовном управлении мусульман Казахстана, которое занимается халал стандартизацией и сертификацией продукции, аккредитовано Малазийским департаментом исламского развития (JAKIM). В результате, он включен в список 75 компаний, занимающихся халал стандартизацией, в которые входят 43 государства мира. К ним относятся США, Великобритания, Китай, Германия, Франция, Италия, Корея, Япония и мусульманские страны, такие как Египет, Пакистан, ОАЭ.</p>\r\n\r\n<p>Департамент исламского развития &nbsp;в Малайзии (JAKIM) является международно признанным учреждением, ответственным за исламские дела, включая сертификацию халал.</p>\r\n\r\n<p>Основная цель учреждения - защита прав потребителей халальных продуктов, предоставление продуктов, отвечающих требованиям канонов шариата, аккредитация учреждений, занимающихся стандартизацией и сертификацией для развития халал промышленности, на долю которой приходится 16,7% мировой доли рынка.</p>\r\n\r\n<p>Сотрудники &laquo;Халал Даму&raquo; 2 года вели интенсивную работу для прохождения аккредитации организации JAKIM. Включение ТОО &laquo;Халал Даму&raquo; в список международных организаций даст толчок к халал-стандартизации в нашей стране. Это отличный способ для потребителей и производителей открыть международный халяльный рынок.</p>\r\n\r\n<p>В своем традиционном обращении к народу президент Нурсултан Назарбаев сказал: &laquo;Укрепление малого и среднего бизнеса является основой экономического роста и обеспечения населения качественной пищей. Здоровье нации является главным приоритетом государства&raquo;. Председатель Духовного управления мусульман Казахстана, Верховный муфтий Серикбай кажы Ораз инициировал ряд хороших мероприятий в области халал-стандартизации.</p>\r\n\r\n<p>Стоит отметить, что с 1 по 10 сентября этого года аудиторы малазийской организации JAKIM посетили отдел стандартизации халяль ДУМК и ознакомились с деятельностью этого учреждения. В то же время были проверены знания аналитика-технолога, инспектора-контролера, мясника, лекторов семинара, специалистов отдела фетв в соответствии с требованиями международного стандарта халал. В результате вся работа, проделанная ТОО &laquo;Халал Даму&raquo;, была проверена на соответствие международным стандартам и получила аккредитацию и высокую оценку.</p>', '', '', 1, 1, 1, '2018-11-27 05:51:00', '2019-05-15 11:53:36'),
(80, 'НАО «Центр поддержки гражданских инициатив»', 'НАО «Центр поддержки гражданских инициатив»', NULL, 'images/uploads/ce3c2eb1cea0c8d7980e4e257d5e6bc9.jpg', 'Конституция страны определяет Республику Казахстан светским государством. В настоящее время в казахстанском обществе осуществляется своеобразный духовный ренессанс, который связан с повышенным интересом к религии, появлением новых форм религиозности.', NULL, NULL, '<p>Конституция страны определяет Республику Казахстан&nbsp;светским государством. В настоящее время в казахстанском обществе осуществляется своеобразный духовный ренессанс, который связан с повышенным интересом к религии, появлением новых форм религиозности.</p>\r\n\r\n<p>В этой связи наибольшую популярность и актуальность приобретает процесс изучения религиозной ситуации, развития различных религиозных институтов, отношения населения в религии и отслеживания негативных процессов, имеющих место в данных вопросах.</p>\r\n\r\n<p>&nbsp;Особенно в этом процессе возрастает роль общественных организаций изучающих данные процессы, и окажет положительное влияние на укрепление общенационального согласия и национального единства.</p>\r\n\r\n<p><strong>Так, в рамках государственного грантового финансирования в 2018 году НАО &laquo;Центр поддержки гражданских инициатив&raquo; при поддержке Министерства общественного развития РК поддержал ряд проектов в сфере религий. Реализация &nbsp;которых &nbsp;позволит в первую очередь изучить состояние религиозной сферы в Казахстане и сформировать комплексные рекомендации и предложения по работе в данном направлении.</strong></p>\r\n\r\n<p>Одним из ключевых проектов в данном вопросе является проект &nbsp;<strong>&laquo;Подготовка обзорного доклада о состоянии религиозной сферы в Казахстане: проблемы и перспективы&raquo;</strong>&nbsp;основная цель проекта изучение состояния религиозной сферы в Казахстане, существующих в данном вопросе проблем и определение дальнейших перспектив развития. Проект реализуется Республиканским общественным объединением &laquo;Контртеррористический комитет&raquo;.</p>\r\n\r\n<p>В рамках данного проекта проведено комплексное исследование по изучению мнения населения, экспертов в сфере религий, &nbsp;проведен &nbsp;сравнительный анализ &nbsp;состояния религиозной ситуации в Республике Казахстан, на основе открытых источников. Особое внимание в рамках проекта уделено&nbsp; &nbsp;анализу контента по механизмам противодействия распространению религиозного экстремизма и радикализма в международной практике &nbsp;и изучение зарубежных практик выявления лиц, имеющих радикальные взгляды, опыт превентивных мер по противодействию радикализму, опыт в реабилитационной работе.&nbsp; Важное значение в проекте уделяется анализу успешных отечественных практик по противодействию распространению религиозного экстремизма и радикализма. В целом в рамках исследования изучено мнение 2500 человек, более 30 экспертов. К проектной деятельности привлечены партнеры из числа общественных организаций, таких как ОФ&laquo;Информационно-консультативная группа &laquo;Перспектива&raquo; и ОО &laquo;Центр развития социальных инноваций и технологий&raquo;. Результаты проекта были презентованы для всех заинтересованных структур из числа представителей государственных органов, НПО и СМИ в г.Алматы 15 ноября 2018 года.</p>\r\n\r\n<p>Немаловажное значение отводится и изучению вопросов развития казахстанской школы исламоведения в виду распространения радикальных религиозных взглядов и неправильного толкования религиозных доктрин. По мнению большинства экспертов в Казахстане произошел процесс слияния ислама с этно-культурными ценностями и традициями казахского народа. Следует также отметить роль ислама в процессе формирования казахской культуры и национального менталитета, который стал неотъемлемым элементом культуры народа.</p>\r\n\r\n<p>Так в рамках государственного грантового финансирования в 2018 году&nbsp; ОФ &laquo;Фонд поддержки духовных ценностей&raquo;&nbsp; реализован проект&nbsp;&nbsp;<strong>&laquo;Проведение исследовательских работ по развитию Казахстанской школы исламоведения&raquo;</strong>. В рамках данного проекта проведено комплексное исследованиепо анализу роли исламоведения в изучении связи Ислама с национальной культурой и разработка теолого-методологических основ толкований Корана и хадисов, отражающих специфику развития Ислама в Казахстане. В рамках данной деятельности к проведению исследовательских работ привлечены представители научного сообщества и эксперты в сфере религий, как Дербісәлі Әбдісаттар, Муминов А.К , Кенжетаев Д. Т., Керім Ш.Т., Мухитдинов Р. С., Қыдыр Т.Е , Исахан М.Б., &nbsp;&nbsp;Әділбаева Ш., Құрманбаев Қ.,Самет Окан, Сатыбалдиева А., а также&nbsp; переводчики &ndash; &nbsp;&nbsp;Бердалиев И., Мустафаева К.М., Имамумади Т.По результатам проекта будет проведено &nbsp;11 тематических исследований и обеспечен перевод 5 зарубежных источников и трудов богословов на государственный язык. Все исследования в рамках проекта войдут в итоговый сборник и будут доступны представителям общественности. Реализация данного проекта позволит расширить знания об этапах и условиях развития казахстанской школы исламоведения и позволит выработать действенные механизмы для правильного формирования идей ислама в Казахстане, и предотвратит возможность развития радикальных идеологических течений.</p>\r\n\r\n<p>Принимая во внимание развитие религиозных верований у населения Казахстана, возникает необходимость в организации работы информационно-пропагандистских групп и повышении квалификации членов ИРГ. В 2018 году данную работу в рамках государственного грантового финансирования проводит Учреждение &laquo;Консультативно-методический центр &laquo;Парасат&raquo; в рамках проекта&nbsp;<strong>&laquo;Организация работы по подготовке и переподготовке членов информационно-разъяснительных групп по вопросам религии&raquo;</strong>. &nbsp;</p>\r\n\r\n<p>Так в рамках данного проекта проведен мониторинг&nbsp; и анализ деятельности информационно-разъяснительных групп по вопросам религий всех уровней в Карагандинской области, Западно-Казахстанской области, Алматинской области, Акмолинской области. В целях модернизации (совершенствования) информационно-разъяснительной работы, включающая в себя оптимизацию качественного и количественного составов ИРГ и выработки единого алгоритма их дальнейшей работы, разработаны и согласованы с КОС МОР РК концепция подготовки и переподготовки членов ИРГ по вопросам религии, учебная программа, из 4-х модулей,&nbsp; с объемом на 40 академических часов, профессиональные требования предъявляемых к члену ИРГ и единого алгоритма&nbsp; работы, материалы для аттестации.&nbsp; Также в рамках проекта организованы&nbsp; выездные курсы, направленные на подготовку и переподготовку членов ИРГ по вопросам религии с охватом представителей всех регионов страны. Разработаны &laquo;Методические рекомендации по организации и проведению информационно-разъяснительной работы среди проблемных категорий населения, подверженной влиянию экстремисткой идеологии&raquo; и справочно-методическое пособие &laquo;Подготовка и переподготовка членов информационно-разъяснительных групп по вопросам религии&raquo;.&nbsp; По результатам проекта создана единая электронная база данных членов ИРГ всех уровней, с информацией, контакты, профиль деятельности, направления и тематики выступлений.</p>\r\n\r\n<p>Таким образом, считаем, что реализация данных проектов позволит сформировать качественно новый подход к работе в сфере религий, и будет содействовать в формировании важных стратегических задач в данном направлении с учетом мнения представителей гражданского общества.</p>\r\n\r\n<p><em><strong>НАО &laquo;Центр поддержки гражданских инициатив&raquo;</strong></em></p>', '', '', 1, 1, 1, '2018-11-26 05:54:00', '2019-05-15 12:07:16'),
(81, 'Верховный муфтий встретился с министром Уакыф Египта', NULL, NULL, 'images/uploads/56f443f93e604fcfafe2077c11cc9672.jpg', 'Во время официального визита в Арабскую Республику Египет Серикбай кажы Ораз, председатель Духовного управления мусульман Казахстана, встретился с министром Уакыф Египта Мухаммедом Мухтаром Джумой.', NULL, NULL, '<p>Во время официального визита в Арабскую Республику Египет Серикбай кажы Ораз, председатель Духовного управления мусульман Казахстана, встретился с министром Уакыф Египта Мухаммедом Мухтаром Джумой.</p>\r\n\r\n<p>В заседании принял участие посол Казахстана в Египте Арман Исагалиев и муфтий Уганды Шагбан Рамадан.<br />\r\nНа встрече основное внимание было уделено единству мусульманской уммы, важности вынесения фетв современных проблем, религиозного образования, развития исламского образования и науки, реализации совместных проектов Духовного управления мусульман Казахстана и Министерства Уакфа.</p>\r\n\r\n<p>&laquo;Казахстан и Египет имеют древнюю историческую связь. У нас один религиозный путь. Один из актуальных вопросов сегодняшнего дня - укрепить единство мусульманской уммы и работать вместе в этом направлении&raquo;, -&nbsp;сказал Верховный муфтий Серикбай кажы Ораз.</p>', '', '', 1, 1, 1, '2018-11-22 05:56:00', '2019-05-15 11:58:31'),
(82, 'В Алматы открылась мечеть имени Кунаева', NULL, NULL, 'images/uploads/4e2807a8cd02ea5c7af35960e4f6025d.jpg', 'Cегодня состоялось торжественное открытие новой мечети имени Динмуxаммеда Аxмедовича Кунаева в микрорайоне Акбулак Алатауского района города Алматы.', NULL, NULL, '<p>Cегодня состоялось торжественное открытие новой мечети имени Динмуxаммеда Аxмедовича Кунаева в микрорайоне Акбулак Алатауского района города Алматы.&nbsp;<br />\r\n&nbsp;<br />\r\nОткрытие началось с прочтения Корана главным имамом Центральной мечети города Алматы.<br />\r\n<br />\r\nЗаместитель председателя ДУМК, наиб муфтий Алау Адильбаев в первую очередь поздравил население Алматы с открытием новой мечети. Он выразил благодарность гражданам отдающим дань памяти Динмуxаммеда Кунаева, &nbsp;который не покладая рук работал во благо своего народа.&nbsp;<br />\r\n&nbsp;<br />\r\nНаиб муфтий вручил Благодарственное письмо и чапан инициаторам строительства данной мечети Абенову Даулету Тогайулы и Халибек Зейноллаулы от имени председателя Дуxовного управления мусульман Казаxстана, верxовного муфтий Серикбай кажы Ораза.&nbsp;<br />\r\n&nbsp;<br />\r\nПосле, Кенес Ауxадиев, бывший сослуживец Динмуxаммеда Аxмедовича, поделился воспоминаниями о достопочтенном сыне ...</p>', '', '', 1, 1, 1, '2018-11-21 05:58:00', '2019-05-15 12:07:32'),
(83, 'Нұр-Мүбарак университетінің студенті ең үздік ғылыми жұмыс байқауының жеңімпазы', 'Нұр-Мүбарак университетінің студенті ең үздік ғылыми жұмыс байқауының жеңімпазы', NULL, 'images/uploads/f0743913f1f026d103b02938914e76f7.jpg', 'Нұр-Мүбарак университетінің «Дінтану» мамандығы бойынша білім алушы студент Назгүл Токова ғылыми зерттеу аясында республикалық байқау жеңімпазы атанды.', 'Нұр-Мүбарак университетінің «Дінтану» мамандығы бойынша білім алушы студент Назгүл Токова ғылыми зерттеу аясында республикалық байқау жеңімпазы атанды.', NULL, '<p>Нұр-Мүбарак университетінің &laquo;Дінтану&raquo; мамандығы бойынша білім алушы студент Назгүл Токова ғылыми зерттеу аясында республикалық байқау жеңімпазы атанды.</p>\r\n\r\n<p>Қазақстан Республикасы &nbsp;Білім және ғылым министрлігі жаратылыстану-техникалық, әлеуметтік-гуманитарлық және экономикалық ғылымдар бойынша еліміздегі жоғары оқу орындарындағы студенттер арасында ғылыми жұмыстарға байқау жариялаған болатын. Осы білім сайысында ең үздік ғылыми жұмысы үшін Назгүл Токова министр Ерлан Сағадиевтің қолымен 3-ші дәрежелі дипломмен марапатталды.</p>\r\n\r\n<p>Ғылыми жетекшілік жасаған PhD, доцент м.а. Альбина Дүйсенбаева. Жеңімпаз дипломант Н.Токова:</p>\r\n\r\n<blockquote>\r\n<p>Бұл ең әуелі өзім білім алып жатқан Нұр-Мүбарак университетінің, &laquo;Дінтану&raquo; кафедрасы ұстаздарының берген білімі мен бағыт-бағдарының жемісі деп білемін,&nbsp;- деп университет пен ұстаздарына алғысын білдірді.</p>\r\n</blockquote>', '<p>Нұр-Мүбарак университетінің &laquo;Дінтану&raquo; мамандығы бойынша білім алушы студент Назгүл Токова ғылыми зерттеу аясында республикалық байқау жеңімпазы атанды.</p>\r\n\r\n<p>Қазақстан Республикасы &nbsp;Білім және ғылым министрлігі жаратылыстану-техникалық, әлеуметтік-гуманитарлық және экономикалық ғылымдар бойынша еліміздегі жоғары оқу орындарындағы студенттер арасында ғылыми жұмыстарға байқау жариялаған болатын. Осы білім сайысында ең үздік ғылыми жұмысы үшін Назгүл Токова министр Ерлан Сағадиевтің қолымен 3-ші дәрежелі дипломмен марапатталды.</p>\r\n\r\n<p>Ғылыми жетекшілік жасаған PhD, доцент м.а. Альбина Дүйсенбаева. Жеңімпаз дипломант Н.Токова:</p>\r\n\r\n<blockquote>\r\n<p>Бұл ең әуелі өзім білім алып жатқан Нұр-Мүбарак университетінің, &laquo;Дінтану&raquo; кафедрасы ұстаздарының берген білімі мен бағыт-бағдарының жемісі деп білемін,&nbsp;- деп университет пен ұстаздарына алғысын білдірді.</p>\r\n</blockquote>', '', 1, 1, 1, '2018-11-21 06:00:00', '2019-05-15 12:02:47'),
(84, 'ПРЕЗИДЕНТ ЕГИПТА НАГРАДИЛ ВЕРХОВНОГО МУФТИЯ ОРДЕНОМ I СТЕПЕНИ', NULL, NULL, 'images/uploads/8288e2640b673700649c995f4a191f4d.jpg', 'Президент Арабской Республики Египет Абдель Фаттах Сиси наградил ряд ученых и мыслителей, которые внесли свой вклад в исламскую науку, в рамках празднования дня рождения Пророка, состоявшегося в университете Аль-Азхар, организованным министерством Уакыф Египта.', NULL, NULL, '<p dir=\"ltr\">Президент Арабской Республики Египет Абдель Фаттах Сиси наградил ряд ученых и мыслителей, которые внесли свой вклад в исламскую науку, в рамках празднования дня рождения Пророка, состоявшегося в университете Аль-Азхар, организованным министерством Уакыф Египта.</p>\r\n\r\n<p dir=\"ltr\">Среди выдающихся деятелей ислама есть и Серикбай кажы Ораз, Верховный муфтий, председатель Духовного управления мусульман Казахстана. Верховный муфтий Серикбай Сатыбалдыулы был награжден орденом науки и искусств I степени.</p>', '', '', 1, 1, 1, '2018-11-21 06:08:00', '2019-05-15 12:10:18'),
(85, 'В Южной Корее развивается халяль-индустрия', NULL, NULL, 'images/uploads/4a4e81a65c9c466de272845140f7175f.jpg', 'Южная Корея стала развивать халяль-туризм после того, как резко снизился поток туристов из Китая, сообщает Religion News Service.', NULL, NULL, '<p>Южная Корея стала развивать халяль-туризм после того, как резко снизился поток туристов из Китая, сообщает Religion News Service.</p>\r\n\r\n<p>В стране предпринимаются новые шаги для привлечения мусульман, хотя у местного населения это вызывает неодобрение.</p>\r\n\r\n<p>Первопричиной развития&nbsp;туризма, ориентированного на мусульман, стал политический кризис в отношениях с США. Китайское правительство при этом было вынуждено запретить своим гражданам въезд в Южную Корею. Из-за этого страна потеряла 6,8 млрд долларов, что вынудило власти Кореи переориентироваться в сфере&nbsp;туризма&nbsp;на мусульманский Восток. Туристические агентства также адаптируют турпакеты под мусульман, создавая для них комфортные условия для пребывания в стране.Государство потратило более 175 000 долларов на создание молитвенных комнат в популярных туристических районах.</p>', '', '', 1, 1, 1, '2018-11-20 06:10:00', '2019-05-15 12:11:54'),
(86, 'Cегодня 16 ноября 2018 года в г. Алматы, прошел круглый стол на тему: «Проведение исследовательских работ по развитию Казахстанской школы исламоведения»', NULL, NULL, 'images/uploads/22c3e0aa2079e96cb8eff0c66d703e44.jpg', 'Организаторами мероприятия являются корпоративный фонд «Фонд поддержки духовных ценностей» в рамках государственного гранта НАО «Центр гражданских инициатив» при поддержке Министерства общественного развития Республики Казахстан в партнерстве с Духовным управлением мусульман Казахстана.', NULL, NULL, '<p>Организаторами мероприятия являются корпоративный фонд &laquo;Фонд поддержки духовных ценностей&raquo; в рамках государственного гранта НАО &laquo;Центр гражданских инициатив&raquo; при поддержке Министерства общественного развития Республики Казахстан в партнерстве с Духовным управлением мусульман Казахстана.&nbsp;</p>\r\n\r\n<p>В рамках круглого стола прошло обсуждение результатов проведенных исследований и внесения в научный оборот новых религиозных источников в рамках реализации проекта &laquo;Проведение исследовательских работ по развитию Казахстанской школы исламоведения&raquo;.&nbsp;</p>\r\n\r\n<p>На данном мероприятии приняли участие известные религиоведы, представители научно-экспертного сообщества и представители Духовного управления мусульман Казахстана.</p>', '', '', 1, 1, 1, '2018-11-19 06:12:00', '2019-05-15 12:14:11'),
(87, 'Бас мүфти \"Уақып\" қорының жаңа президентін қабылдады', 'Бас мүфти \"Уақып\" қорының жаңа президентін қабылдады', NULL, 'images/uploads/c4db83c42be0463db7c7a7f0c8b29f3b.jpg', 'Қазақстан мұсылмандары діни басқармасының төрағасы Серікбай қажы Ораз жуырда тағайындалған \"Уақып\" қайырымдылық қорының жаңа президенті Қайрат Әзімовпен кездесті.', 'Қазақстан мұсылмандары діни басқармасының төрағасы Серікбай қажы Ораз жуырда тағайындалған \"Уақып\" қайырымдылық қорының жаңа президенті Қайрат Әзімовпен кездесті.', NULL, '<p>Қазақстан мұсылмандары діни басқармасының төрағасы Серікбай қажы Ораз жуырда тағайындалған &quot;Уақып&quot; қайырымдылық қорының жаңа президенті Қайрат Әзімовпен кездесті.</p>\r\n\r\n<p>Бас мүфти Қайрат Мұратұлын жаңа қызметімен құттықтап, жұмысына игілік пен береке тіледі.&nbsp;Кездесуде дін қызметкерлерінің әлеуметтік мәселесін толыққанды шешу, қайырымдылық бағытында республикалық маңызды іс-шараларды бірлесе атқару, білім, ғылым, діни ағарту жолында біріккен жобаларды жүзеге асыру, т.б. мәселелер талқыланды.</p>\r\n\r\n<p>Серікбай қажы Сатыбалдыұлы 350-ден астам дін қызметкерінің&nbsp;&quot;Уақып&quot; қайырымдылық қоры есебінен арнайы стипендия алып жатқанын жеткізіп, қордың басшылық құрамына алғысын жеткізді.</p>\r\n\r\n<p>Өз кезегінде сөз алған Қ.Әзімов еш жерден жалақы алмайтын дін қызметкерлерінің әлеуметтік мәселесін шешу бағытында оң шешім қабылданатынын мәлімдеді.&nbsp;</p>\r\n\r\n<p>Басқосу барысында Бас мүфти Серікбай қажы Ораз Қазақстан мұсылмандары діни басқармасын қаржыландыруға қатысты жобалармен бөлісті. Сонымен қатар, ҚМДБ &quot;Қажылық&quot; қоры есебінен 100 имамға жалақы тағайындалғанын жеткізді.</p>\r\n\r\n<p>Айта кетейік, еліміз бойынша 6189 дін қызметкері жұмыс істейді. Оларды толыққанды жалақымен қамтамасыз ету бағытында арнайы жобалар жүзеге асуда.</p>', '<p>Қазақстан мұсылмандары діни басқармасының төрағасы Серікбай қажы Ораз жуырда тағайындалған &quot;Уақып&quot; қайырымдылық қорының жаңа президенті Қайрат Әзімовпен кездесті.</p>\r\n\r\n<p>Бас мүфти Қайрат Мұратұлын жаңа қызметімен құттықтап, жұмысына игілік пен береке тіледі.&nbsp;Кездесуде дін қызметкерлерінің әлеуметтік мәселесін толыққанды шешу, қайырымдылық бағытында республикалық маңызды іс-шараларды бірлесе атқару, білім, ғылым, діни ағарту жолында біріккен жобаларды жүзеге асыру, т.б. мәселелер талқыланды.</p>\r\n\r\n<p>Серікбай қажы Сатыбалдыұлы 350-ден астам дін қызметкерінің&nbsp;&quot;Уақып&quot; қайырымдылық қоры есебінен арнайы стипендия алып жатқанын жеткізіп, қордың басшылық құрамына алғысын жеткізді.</p>\r\n\r\n<p>Өз кезегінде сөз алған Қ.Әзімов еш жерден жалақы алмайтын дін қызметкерлерінің әлеуметтік мәселесін шешу бағытында оң шешім қабылданатынын мәлімдеді.&nbsp;</p>\r\n\r\n<p>Басқосу барысында Бас мүфти Серікбай қажы Ораз Қазақстан мұсылмандары діни басқармасын қаржыландыруға қатысты жобалармен бөлісті. Сонымен қатар, ҚМДБ &quot;Қажылық&quot; қоры есебінен 100 имамға жалақы тағайындалғанын жеткізді.</p>\r\n\r\n<p>Айта кетейік, еліміз бойынша 6189 дін қызметкері жұмыс істейді. Оларды толыққанды жалақымен қамтамасыз ету бағытында арнайы жобалар жүзеге асуда.</p>', '', 1, 1, 1, '2018-11-16 00:15:00', '2019-05-15 12:18:35'),
(88, 'Сотрудники муфтията написали всенародный диктант на латинице', NULL, NULL, 'images/uploads/ceb917e51829ee58656786c5fd85cc59.jpg', 'Духовное управление мусульман Казахстана поддержало мероприятие «Всенародный диктант», организованное Министерством культуры и спорта.', NULL, NULL, '<p>Духовное управление мусульман Казахстана поддержало мероприятие &laquo;Всенародный диктант&raquo;, организованное Министерством культуры и спорта.</p>\r\n\r\n<p>Шакирты медресе и работники мечетей страны одновременно писали диктант в новом казахском алфавите.</p>\r\n\r\n<p>Стоит отметить, что мероприятие &quot;Всенародный диктант&quot; проводилось в рамках перевода казахского алфавита на латинскую графику. Диктант был организован в специальных аудиториях, подготовленных региональными отделами развития языков по всей стране. Диктант был написан на тему &laquo;Духовное возрождение&raquo;. Инициативу Министерства культуры и спорта Республики Казахстан также поддержали общественные деятели, писатели, ученые, спортсмены, деятели культуры и искусства и журналисты.</p>', '', '', 1, 1, 1, '2018-11-16 06:19:00', '2019-05-15 12:20:53'),
(89, '16 ноября 2018 года в Алматы запланировано проведение круглого стола на тему: «Проведение исследовательских работ по развитию Казахстанской школы исламоведения».', NULL, NULL, 'images/uploads/4fe4f3cfd869b7de79f3dc3d57e2b4f8.jpg', 'Организаторами мероприятия являются корпоративный фонд «Фонд поддержки духовных ценностей» в рамках государственного гранта НАО «Центр гражданских инициатив» при поддержке Министерства общественного развития Республики Казахстан в партнерстве с Духовным управлением мусульман Казахстана.', NULL, NULL, '<p>Организаторами мероприятия являются корпоративный фонд &laquo;Фонд поддержки духовных ценностей&raquo; в рамках государственного гранта НАО &laquo;Центр гражданских инициатив&raquo; при поддержке Министерства общественного развития Республики Казахстан в партнерстве с Духовным управлением мусульман Казахстана.</p>\r\n\r\n<p>В рамках круглого стола запланировано обсуждение результатов проведенных исследований и внесения в научный оборот новых религиозных источников в рамках реализации проекта &laquo;Проведение исследовательских работ по развитию Казахстанской школы исламоведения&raquo;.</p>\r\n\r\n<p>В качестве спикеров данного мероприятия выступят Абсаттар Дербисали, Алау Адилбаев, Досай Кенжетай, Мукан Исахан, Имамумади Тусуфхан, Кайрат Курманбаев, Рашид Мухитдинов, Акмарал Сатыбалдиева, Тореали Кыдыр, Камшат Мустафаева и др.</p>\r\n\r\n<p>В работе круглого стола примут участие известные религиоведы, представители научно-экспертного сообщества и представители Духовного управления мусульман Казахстана.</p>\r\n\r\n<p>Мероприятие состоится в конференц-зале Египетского университета исламской культуры Нур-Мубарак (205 кабинет), улица Аль-Фараби 73, г. Алматы, ост.&nbsp;&laquo;Академгородок&raquo; с 14.00 до 17.00 часов.</p>\r\n\r\n<p>По дополнительным вопросам просим обращаться по телефонам:&nbsp;8 (7172)74-09-51,&nbsp;8775-888-04-03.</p>', '', '', 1, 1, 1, '2018-11-14 06:21:00', '2019-05-15 12:22:35'),
(90, 'Назначен новый ПрезидентКазахстанского Благотворительного фонда «УАҚЫП» Азимов Кайрат Муратович', NULL, NULL, 'images/uploads/36a6faa2aeed3d28a4375de88c3a146f.jpg', 'АСТАНА, 5ноября 2018 года, – В ходе заседания Попечительского Совета  Корпоративного Благотворительного Фонда «Казахстанский фонд «Уақып», решениемчленов Попечительского Совета Азимов Кайрат Муратович назначен Президентом Казахстанского Благотворительного Фонда «УАҚЫП».', NULL, NULL, '<p>АСТАНА,&nbsp;5ноября&nbsp;2018 года,&nbsp;&ndash;&nbsp;В ходе заседания Попечительского Совета&nbsp;&nbsp;Корпоративного Благотворительного Фонда &laquo;Казахстанский фонд &laquo;Уақып&raquo;,&nbsp;решениемчленов&nbsp;Попечительского Совета&nbsp;Азимов Кайрат Муратович&nbsp;назначен&nbsp;Президентом Казахстанского Благотворительного Фонда &laquo;УАҚЫП&raquo;.</p>\r\n\r\n<p>К. Азимов&nbsp;имеет достаточный опыт в&nbsp;дипломатической сфере, работал на различных должностях в МИД Республики Казахстан, в Посольстве РК в Российской Федераций, в Генеральной консульстве РК в г.Дубай и СеверныхЭмиратах.&nbsp;Имел опыт работы&nbsp;по привлечению инвестиций&nbsp;в АО &laquo;Национальная компания &laquo;KAZAKH INVEST&raquo;.&nbsp;Владеет казахским, русским, арабским, английским языками.</p>\r\n\r\n<p>Имеет множествоблагодарственных писем от Министра иностранных дел РК Ответственного секретаря МИД РК, Руководства МГУ им. М.Ломоносова, Президента Федеральной национально-культурной автономии казахов Российской Федераций и Ректора Российского университета дружбы народов.</p>\r\n\r\n<p>Благотворительный Фонда &laquo;УАҚЫП&raquo; создан по поручению Президента Республики Казахстан Н.А. Назарбаева 1 июля 2017 года. Основной целью Фонда является всемерная поддержка в реализации государственной политики в сфере религии, изложенной в Концепции государственной политики в религиозной сфере Республики Казахстан на 2017 &ndash; 2020 годы.</p>', '', '', 1, 1, 1, '2018-11-14 06:23:00', '2019-05-15 12:24:49'),
(91, '4D-музей Корана откроется в Индонезии', NULL, NULL, 'images/uploads/2e1f7fb4d9220fc14fff95e7e1ed3366.jpg', 'В индонезийском городе Маланге планируется построить исламский центр, главной достопримечательностью которого  которого станет музей Корана в 4-D формате, сообщает портал Malang Times.', NULL, NULL, '<article>\r\n<p>В индонезийском городе Маланге планируется построить исламский центр, главной достопримечательностью которого&nbsp; которого станет музей Корана в 4-D формате, сообщает портал Malang Times.</p>\r\n\r\n<p>В музее, акцент будет сделан не на внешних параметрах, а на содержании Книги Аллаха. В качестве примера взят дизайн Музея Этихад в Дубае. Планируется создать аналогичный дизайн в 4D формате, что позволит отобразить содержание и смыслы аятов на более качественном уровне.</p>\r\n\r\n<p>Проект строительства музея Корана в&nbsp;Индонезии&nbsp;рассчитан на 2019-2023 гг. На проект исламского центра, включая музей, правительством Индонезии выделено 5,56 га земли и 450 млрд. рупий ($ 29,69 млн.).</p>\r\n\r\n<p>Строительство музея Корана входит в правительственную программу Индонезии по развитию халяль-туризма в регионе.&nbsp;</p>\r\n</article>', '', '', 1, 1, 1, '2018-11-13 06:25:00', '2019-05-15 12:26:24'),
(92, 'ИМАМДАР ЭЛЕКТРОНДЫ ҮЛГІДЕ АТТЕСТАЦИЯДАН ӨТТІ', 'ИМАМДАР ЭЛЕКТРОНДЫ ҮЛГІДЕ АТТЕСТАЦИЯДАН ӨТТІ', NULL, 'images/uploads/98e37755fb6a236e23a10cf56de3af97.jpg', 'Қазақстан мұсылмандары діни басқармасының төрағасы Серікбай қажы Ораз Тараз қаласында Сараптау комиссиясының жұмысына қатысты.', 'Қазақстан мұсылмандары діни басқармасының төрағасы Серікбай қажы Ораз Тараз қаласында Сараптау комиссиясының жұмысына қатысты.', NULL, '<p>Қазақстан мұсылмандары діни басқармасының төрағасы Серікбай қажы Ораз Тараз қаласында Сараптау комиссиясының жұмысына қатысты.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Аттестация мақсаты - дін қызметкерінің білімі мен қабілетін айқындау. Білімін дəлелдей алмаған имамды қайта оқыту курсынан өткіземіз&quot;, - деп атап өтті Серікбай қажы Ораз.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Айта кетейік, Жамбыл облысына қарасты 400-ден астам дін қызметкері арнайы əзірленген электронды тест жүйесімен сынақ тапсырды. ҚМДБ Діни істер бөлімінің меңгерушісі Нұрбек Есмағанбеттің айтуынша, аталған жоба Сараптау комиссиясының жұмыс тиімділігін арттыруда.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Электронды тест сұрақтарында ақида,&nbsp; фиқһ, заң, ҚМДБ құрылымына қатысты сауалдар енгізілген. Имамдар бұған қоса ауызша емтиханнан өтті&quot;, - дейді Н.Əуезханұлы.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Сараптау комиссиясына наиб мүфти Алау Əділбай төрағалық етті.</p>', '<p>Қазақстан мұсылмандары діни басқармасының төрағасы Серікбай қажы Ораз Тараз қаласында Сараптау комиссиясының жұмысына қатысты.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Аттестация мақсаты - дін қызметкерінің білімі мен қабілетін айқындау. Білімін дəлелдей алмаған имамды қайта оқыту курсынан өткіземіз&quot;, - деп атап өтті Серікбай қажы Ораз.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Айта кетейік, Жамбыл облысына қарасты 400-ден астам дін қызметкері арнайы əзірленген электронды тест жүйесімен сынақ тапсырды. ҚМДБ Діни істер бөлімінің меңгерушісі Нұрбек Есмағанбеттің айтуынша, аталған жоба Сараптау комиссиясының жұмыс тиімділігін арттыруда.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Электронды тест сұрақтарында ақида,&nbsp; фиқһ, заң, ҚМДБ құрылымына қатысты сауалдар енгізілген. Имамдар бұған қоса ауызша емтиханнан өтті&quot;, - дейді Н.Əуезханұлы.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Сараптау комиссиясына наиб мүфти Алау Əділбай төрағалық етті.</p>', '', 1, 1, 1, '2018-11-09 06:26:00', '2019-05-15 12:29:00'),
(93, 'В Дубае открывает очередной сезон цветочный сад чудес', NULL, NULL, 'images/uploads/dddbe164f2ce5571ba7a351f95e9794b.jpg', 'Дубайский сад чудес (Dubai Miracle Garden) открывает сегодня, 1 ноября, 7-й сезон работы. В этом году гостей ждут цветочные композиции в виде героев мультфильмов Диснея: Микки и Мини Мауса, Дональда и Дейзи Дака, Гуфи, Плуто и других.', NULL, NULL, '<p>Дубайский сад чудес (Dubai Miracle Garden) открывает сегодня, 1 ноября, 7-й сезон работы. В этом году гостей ждут цветочные композиции в виде героев мультфильмов Диснея: Микки и Мини Мауса, Дональда и Дейзи Дака, Гуфи, Плуто и других.</p>\r\n\r\n<p>В среднем каждая скульптура весит около 5 тонн, на ее изготовление каждой из них ушло 30 тысяч цветов. Скульптура Микки Мауса имеет высоту 18 метров (ранее она была включена в Книгу рекордов Гиннесса) &ndash; в этом году персонаж отмечает 90 лет со дня создания.</p>\r\n\r\n<p>В числе новинок этого года &ndash; гигантские скульптуры кошек, которые встречают гостей у ворот; три веселых &laquo;поливочных&raquo; слона; а также секции &laquo;Потерянный рай&raquo;, &laquo;Цветочный замок&raquo;, амфитеатр для живых представлений и шоу и другие.</p>\r\n\r\n<p>В общей сложности, в этом году в саду было высажено 250 млн цветов и 50 млн растений, появились 2 озера с фонтанами и 3D-скульптурами, а также гамаки для отдыха, сообщает&nbsp;Khaleej Times&nbsp;</p>', '', '', 1, 1, 1, '2018-11-07 06:30:00', '2019-05-15 12:32:00');
