INSERT INTO `pages` (`id`, `title_ru`, `title_kk`, `title_en`, `desc_ru`, `desc_kk`, `desc_en`, `status`, `created_at`, `updated_at`) VALUES
(1, 'О фонде', 'Қор туралы', 'About the fund', '<p class=\"f-24\">1 июля 2017 года на основании указа Президента Республики Казахстан был создан Корпоративный благотворительный фонд &laquo;Казахстанский фонд &laquo;УАҚЫП&raquo;. Основным направлением деятельности Фонда является оказание финансовой благотворительной помощи имамам.</p>', '<p class=\"f-24\">2017 жылдың 1 шілдесінде Қазақстан Республикасы Президентінің жарлығымен &laquo;Қазақстандық &laquo;УАҚЫП&raquo; Корпоративтік қайырымдылық қоры&raquo; құрылды. Қордың негізгі қызметі имамдардың қаржылық қайырымдылықтарын қамтамасыз ету болып табылады.</p>', '<p class=\"f-24\">On July 1, 2017, on the basis of a decree of the President of the Republic of Kazakhstan, the Corporate Charitable Foundation &ldquo;Kazakhstan Foundation &ldquo;WAQYP&rdquo; was established. The main activity of the Foundation is to provide financial charity to the imams.</p>', 1, '2019-03-18 15:22:00', '2019-05-22 11:14:36');
INSERT INTO `pages` (`id`, `title_ru`, `title_kk`, `title_en`, `desc_ru`, `desc_kk`, `desc_en`, `status`, `created_at`, `updated_at`) VALUES
(2, 'Публичная оферта', 'Ашық ұсыныс', 'Public offer', '<div class=\"WordSection1\">\r\n<p align=\"center\" style=\"margin-top:4.45pt; margin-right:186.45pt; margin-bottom:.0001pt; margin-left:186.2pt; text-align:center; margin:0cm 0cm 0.0001pt\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\">ПУБЛИЧНАЯ ОФЕРТА</span></span></i></span></span></span></p>\r\n\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: center;\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\">о добровольном благотворительном пожертвовании</span></span></i></span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:.2pt; margin-right:0cm; margin-bottom:.0001pt; margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<p style=\"margin-top:0cm; margin-right:5.95pt; margin-bottom:.0001pt; margin-left:5.85pt; text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\"><span style=\"letter-spacing:-.05pt\">Корпора</span></span></span></span></i><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\"><span style=\"letter-spacing:.1pt\">т</span></span></span></span></i><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">ивный</span></span></span></i><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\"> &nbsp;<span style=\"letter-spacing:.05pt\">б</span><span style=\"letter-spacing:-.05pt\">лаготвори</span><span style=\"letter-spacing:.1pt\">т</span>ельный &nbsp;<span style=\"letter-spacing:-.05pt\">фон</span>д &nbsp;<span style=\"letter-spacing:.1pt\">&laquo;</span><span style=\"letter-spacing:-.05pt\">Казахстански</span>й &nbsp;<span style=\"letter-spacing:-.05pt\">фон</span>д &nbsp;<span style=\"letter-spacing:.1pt\">&laquo;</span><span style=\"letter-spacing:-.05pt\">У</span><span style=\"letter-spacing:.45pt\">А</span><span style=\"letter-spacing:-.65pt\">Қ</span><span style=\"letter-spacing:.1pt\">ЫП</span>&raquo;</span></span></span></i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">,</span></span> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">в </span></span><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">лице Президента Азимова Кайрата Муратовича, действующего на основании Устава, именуемый в дальнейшем </span></span><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">&laquo;Благополучатель&raquo; </span></span></span></i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">публикует настоящий договор, являющийся публичным договором-офертой (предложением) и предлагает неопределенному кругу лиц (физическим и юридическим лицам), именуемым в дальнейшем </span></span><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">&laquo;Благотворитель&raquo;, </span></span></span></i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">заключить договор о добровольном благотворительном пожертвовании на нижеследующих условиях:</span></span></span></span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:.4pt; margin-right:0cm; margin-bottom:.0001pt; margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"margin-top:0cm; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель по настоящему договору безвозмездно передает Благополучателю денежные средства (благотворительное пожертвование) на благотворительные цели, в том числе на цели, указанные в пунктах 2, 3 настоящего договора.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-right:6.2pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Участие Благотворителя в благотворительной деятельности направлено на поддержку благотворительной деятельности Благополучателя.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.1pt; margin-right:5.8pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворительное пожертвование используется Благополучателем в благотворительных целях, определенных Уставом и Благотворительной программой Благополучателя, а также в целях обеспечения административно- хозяйственной деятельности Благополучателя.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благополучатель публикует информацию о своей работе, целях и задачах, мероприятиях и результатах на официальном сайте </span></span><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">Корпоративного благотворительного фонда&nbsp; &laquo;Казахстанский&nbsp; фонд&nbsp; &laquo;УАҚЫП&raquo;&nbsp; </span></span></span></i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">&ndash;<u style=\"text-underline:blue\"> </u></span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz</span></span></span> </a><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">и/или в других открытых источниках.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.15pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Датой полного и безоговорочного акцепта (принятия предложения) настоящей оферты является дата поступления денежных средств Благотворителя на расчетный счет Благополучателя либо, в соответствующих случаях, на счет Благополучателя в платежной системе; а также дата отправки короткого текстового сообщения (SMS).</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Акцепт данного предложения Благотворителем означает, что Благотворитель ознакомился и полностью согласен со всеми положениями и условиями&nbsp; настоящего предложения, и равносилен заключению договора о добровольном благотворительном пожертвовании.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-right:5.95pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Принимая условия данного предложения, Благотворитель подтверждает добровольный и безвозмездный характер благотворительного пожертвования.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.1pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Моментом заключения договора и вступления его в силу считается момент поступления на расчетный счет&nbsp; Благополучателя&nbsp; перечисленных&nbsp; Благотворителем денежных средств в качестве благотворительного&nbsp; пожертвования.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-right:5.95pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благополучатель готов заключать&nbsp; договоры&nbsp; пожертвования&nbsp; в&nbsp; ином&nbsp; порядке и (или) на иных условиях, нежели это предусмотрено&nbsp; настоящей&nbsp; офертой, для чего любое заинтересованное лицо вправе обратиться для заключения соответствующего договора в офис Благополучателя.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-right:6.15pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Размер безвозмездно передаваемых Благотворителем денежных средств (благотворительного пожертвования), определяется Благотворителем самостоятельно в одностороннем порядке на&nbsp; свое&nbsp; усмотрение&nbsp; и&nbsp; без&nbsp; согласования с Благополучателем.</span></span></span></span></span></span></span></li>\r\n</ol>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"WordSection2\">\r\n<ol start=\"11\">\r\n	<li style=\"margin-top:4.5pt; margin-right:6.1pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt 161.05pt 254.65pt 407.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; передает&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; благотворительное&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; пожертвование Благополучателю, указанное в п.1 настоящего договора добровольно, бескорыстно и на безвозвратной основе.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.2pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель может оформить на сайте </span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz</span></span></span></a> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">поручение на регулярное (ежемесячное) списание пожертвований с банковской карты:</span></span></span></span></span></span></span>\r\n	<ol>\r\n		<li style=\"margin-top:.1pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:41.4pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель в соответствующей форме на сайте </span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz</span></span></span></a> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">может выбрать сумму регулярного списания из предлагаемых вариантов или внести свою.</span></span></span></span></span></span></span></li>\r\n		<li style=\"margin-top:.15pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:40.8pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Поручение считается оформленным после успешного завершения первого списания с карты и получения уведомления об успешном списании на электронный адрес Благотворителя, указанный при оформлении поручения.</span></span></span></span></span></span></span></li>\r\n		<li style=\"margin-top:.2pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:46.8pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Поручение на регулярное списание действует до окончания срока действия карты владельца или до подачи Благотворителем письменного уведомления о прекращении действия поручения на электронный адрес Благополучателя </span></span><a href=\"mailto:info@waqyp.kz\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">info@waqyp.kz</span></span></span></a> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">не менее, чем за 15 календарных дней до даты очередного автоматического списания. Уведомление должно содержать следующие данные: фамилия и имя Благотворителя, как указано на карте, с которой осуществляется автоматическое списание средств; четыре последних цифры карты; электронный адрес, на который Благотворитель получит подтверждение о прекращении регулярного списания.</span></span></span></span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li style=\"margin-top:.5pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель дает разрешение на обработку и хранение своих персональных данных, используемых Благополучателем исключительно для исполнения указанного договора. Благополучатель обязуется не раскрывать третьим лицам личную и контактную информацию Благотворителя без его согласия.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.3pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благополучатель принимает благотворительное пожертвование с благодарностью.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.1pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Настоящая оферта вступает в силу со дня ее&nbsp; публикации&nbsp; на&nbsp; официальном </span></span><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">сайте </span></span><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">Корпоративного благотворительный фонд &laquo;Казахстанский фонд &laquo;УАҚЫП&raquo;</span></span></span></i></span></span></span></span></span></li>\r\n</ol>\r\n\r\n<p class=\"MsoBodyText\" style=\"margin-top:.05pt; margin-right:6.35pt; margin-bottom:.0001pt; margin-left:5.85pt; margin:0cm 0cm 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:12pt\"><span style=\"line-height:116%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">&ndash; <a href=\"http://www.waqyp.kz/\"><span style=\"color:blue\">www.waqyp.kz</span><span style=\"text-decoration:none\"><span style=\"text-underline:none\">.</span></span></a> Условия настоящей оферты также могут быть опубликованы и&nbsp;&nbsp;&nbsp;&nbsp; в других средствах массовой информации.</span></span></span></span></p>\r\n\r\n<ol start=\"16\">\r\n	<li style=\"margin-top:.15pt; margin-right:6.1pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Настоящая оферта является бессрочной и действует до дня, следующего за днем размещения на официальном сайте Благополучателя и в других средствах массовой информации извещения об отмене настоящей оферты.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.2pt; margin-right:6.0pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Условия настоящей оферты могут быть изменены Благополучателем без предварительного уведомления и действуют со дня, следующего за днем их размещения на официальном сайте Благополучателя </span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz</span></span></span><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"text-decoration:none\"><span style=\"text-underline:none\">,</span></span></span></span></a><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"> а также других средствах массовой информации.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.2pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Настоящий договор составлен в единственном экземпляре и опубликован Благополучателем на сайте </span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz,</span></span></span></a> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">как публичная оферта в соответствии со ст. 395 Гражданского кодекса Республики Казахстан.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.15pt; margin-right:6.0pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Реквизиты для перечисления денежных средств, а также&nbsp; возможные способы перечисления благотворительного пожертвования публикуются ниже, а также на официальном сайте Благополучателя и в других средствах массовой информации.</span></span></span></span></span></span></span></li>\r\n</ol>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol start=\"20\">\r\n	<li align=\"left\" style=\"margin-top:4.45pt; margin-right:0cm; margin-bottom:.0001pt; text-align:left; margin:0.25pt 6.05pt 0.0001pt 5.85pt\"><span style=\"font-size:11pt\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\">Адрес и реквизиты Благополучателя:</span></span></i></span></span></span></li>\r\n</ol>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:2.4pt; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Корпоративный благотворительный фонд &laquo;Казахстанский фонд &laquo;УАҚЫП&raquo;</span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:2.5pt; margin-right:6.25pt; margin-bottom:.0001pt; margin-left:5.85pt; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span style=\"line-height:102%\"><span style=\"tab-stops:103.4pt 152.1pt 232.0pt 308.85pt 328.85pt 385.85pt 433.2pt 482.85pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Юридический&nbsp;&nbsp;&nbsp;&nbsp; адрес:&nbsp;&nbsp;&nbsp;&nbsp; Республика&nbsp;&nbsp;&nbsp; Казахстан,&nbsp;&nbsp;&nbsp;&nbsp; г.&nbsp;&nbsp; Астана,&nbsp;&nbsp;&nbsp; район&nbsp;&nbsp;&nbsp;&nbsp; Есиль,&nbsp;&nbsp;&nbsp; пр. Кабанбай батыра, дом 28, офис 5.</span></span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-right:208.65pt; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span style=\"line-height:101%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Президент Фонда Азимов Кайрат Муратович. БИН 170740000147</span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">КБе 18</span></span></p>\r\n\r\n<p class=\"MsoBodyText\" style=\"margin-top:.1pt; margin-right:282.55pt; margin-bottom:.0001pt; margin-left:5.85pt; margin:0cm 0cm 0.0001pt 5.85pt; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"line-height:101%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">ИИК KZ136010111000051511 (KZT) ИИК KZ746010111000051771 (RUB) ИИК KZ316010111000051769 (USD) ИИК KZ046010111000051770 (EUR) БИК HSBKKZKX</span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span style=\"line-height:13.75pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">АО &laquo;Народный Банк Казахстана&raquo;, Астанинский региональный филиал.</span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:.2pt; margin-right:0cm; margin-bottom:.0001pt; margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span style=\"tab-stops:41.7pt 128.5pt 149.85pt 245.6pt 281.05pt 301.8pt 387.5pt 451.9pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Для&nbsp;&nbsp;&nbsp;&nbsp; физических&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; и&nbsp;&nbsp;&nbsp;&nbsp; юридических&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; лиц&nbsp;&nbsp;&nbsp;&nbsp; в&nbsp;&nbsp;&nbsp;&nbsp; назначении&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; платежа&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; указать:</span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:2.45pt; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">&laquo;благотворительное пожертвование на уставные цели&raquo;.</span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:.25pt; margin-right:0cm; margin-bottom:.0001pt; margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:5.85pt; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:11pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\"><span style=\"letter-spacing:-.05pt\">Президен</span></span></span></i><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\">т <span style=\"letter-spacing:-.05pt\">КБ</span>Ф &laquo;К<span style=\"letter-spacing:-.15pt\">а</span>захстанс<span style=\"letter-spacing:.1pt\">к</span>ий <span style=\"letter-spacing:-.05pt\">фон</span>д &laquo;У<span style=\"letter-spacing:.45pt\">А</span><span style=\"letter-spacing:-.65pt\">Қ</span><span style=\"letter-spacing:.1pt\">ЫП</span>&raquo;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style=\"letter-spacing: -0.0666667px;\">Азимов К. М.</span></span></span></i></span></span></p>', '<div class=\"WordSection1\">\r\n<p align=\"center\" style=\"margin-top:4.45pt; margin-right:186.45pt; margin-bottom:.0001pt; margin-left:186.2pt; text-align:center; margin:0cm 0cm 0.0001pt\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\">ПУБЛИЧНАЯ ОФЕРТА</span></span></i></span></span></span></p>\r\n\r\n<p style=\"margin:0cm 0cm 0.0001pt; text-align:center\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\">о добровольном благотворительном пожертвовании</span></span></i></span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:.2pt; margin-right:0cm; margin-bottom:.0001pt; margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<p style=\"margin-top:0cm; margin-right:5.95pt; margin-bottom:.0001pt; margin-left:5.85pt; text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\"><span style=\"letter-spacing:-.05pt\">Корпора</span></span></span></span></i><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\"><span style=\"letter-spacing:.1pt\">т</span></span></span></span></i><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">ивный</span></span></span></i><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\"> &nbsp;<span style=\"letter-spacing:.05pt\">б</span><span style=\"letter-spacing:-.05pt\">лаготвори</span><span style=\"letter-spacing:.1pt\">т</span>ельный &nbsp;<span style=\"letter-spacing:-.05pt\">фон</span>д &nbsp;<span style=\"letter-spacing:.1pt\">&laquo;</span><span style=\"letter-spacing:-.05pt\">Казахстански</span>й &nbsp;<span style=\"letter-spacing:-.05pt\">фон</span>д &nbsp;<span style=\"letter-spacing:.1pt\">&laquo;</span><span style=\"letter-spacing:-.05pt\">У</span><span style=\"letter-spacing:.45pt\">А</span><span style=\"letter-spacing:-.65pt\">Қ</span><span style=\"letter-spacing:.1pt\">ЫП</span>&raquo;</span></span></span></i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">,</span></span> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">в </span></span><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">лице Президента Айтбай Қайрата Орынбайұлы, действующего на основании Устава, именуемый в дальнейшем </span></span><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">&laquo;Благополучатель&raquo; </span></span></span></i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">публикует настоящий договор, являющийся публичным договором-офертой (предложением) и предлагает неопределенному кругу лиц (физическим и юридическим лицам), именуемым в дальнейшем </span></span><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">&laquo;Благотворитель&raquo;, </span></span></span></i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">заключить договор о добровольном благотворительном пожертвовании на нижеследующих условиях:</span></span></span></span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:.4pt; margin-right:0cm; margin-bottom:.0001pt; margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"margin-top:0cm; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель по настоящему договору безвозмездно передает Благополучателю денежные средства (благотворительное пожертвование) на благотворительные цели, в том числе на цели, указанные в пунктах 2, 3 настоящего договора.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-right:6.2pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Участие Благотворителя в благотворительной деятельности направлено на поддержку благотворительной деятельности Благополучателя.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.1pt; margin-right:5.8pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворительное пожертвование используется Благополучателем в благотворительных целях, определенных Уставом и Благотворительной программой Благополучателя, а также в целях обеспечения административно- хозяйственной деятельности Благополучателя.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благополучатель публикует информацию о своей работе, целях и задачах, мероприятиях и результатах на официальном сайте </span></span><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">Корпоративного благотворительного фонда&nbsp; &laquo;Казахстанский&nbsp; фонд&nbsp; &laquo;УАҚЫП&raquo;&nbsp; </span></span></span></i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">&ndash;<u style=\"text-underline:blue\"> </u></span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz</span></span></span> </a><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">и/или в других открытых источниках.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.15pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Датой полного и безоговорочного акцепта (принятия предложения) настоящей оферты является дата поступления денежных средств Благотворителя на расчетный счет Благополучателя либо, в соответствующих случаях, на счет Благополучателя в платежной системе; а также дата отправки короткого текстового сообщения (SMS).</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Акцепт данного предложения Благотворителем означает, что Благотворитель ознакомился и полностью согласен со всеми положениями и условиями&nbsp; настоящего предложения, и равносилен заключению договора о добровольном благотворительном пожертвовании.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-right:5.95pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Принимая условия данного предложения, Благотворитель подтверждает добровольный и безвозмездный характер благотворительного пожертвования.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.1pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Моментом заключения договора и вступления его в силу считается момент поступления на расчетный счет&nbsp; Благополучателя&nbsp; перечисленных&nbsp; Благотворителем денежных средств в качестве благотворительного&nbsp; пожертвования.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-right:5.95pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благополучатель готов заключать&nbsp; договоры&nbsp; пожертвования&nbsp; в&nbsp; ином&nbsp; порядке и (или) на иных условиях, нежели это предусмотрено&nbsp; настоящей&nbsp; офертой, для чего любое заинтересованное лицо вправе обратиться для заключения соответствующего договора в офис Благополучателя.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-right:6.15pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Размер безвозмездно передаваемых Благотворителем денежных средств (благотворительного пожертвования), определяется Благотворителем самостоятельно в одностороннем порядке на&nbsp; свое&nbsp; усмотрение&nbsp; и&nbsp; без&nbsp; согласования с Благополучателем.</span></span></span></span></span></span></span></li>\r\n</ol>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"WordSection2\">\r\n<ol start=\"11\">\r\n	<li style=\"margin-top:4.5pt; margin-right:6.1pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt 161.05pt 254.65pt 407.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; передает&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; благотворительное&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; пожертвование Благополучателю, указанное в п.1 настоящего договора добровольно, бескорыстно и на безвозвратной основе.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.2pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель может оформить на сайте </span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz</span></span></span></a> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">поручение на регулярное (ежемесячное) списание пожертвований с банковской карты:</span></span></span></span></span></span></span>\r\n	<ol>\r\n		<li style=\"margin-top:.1pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:41.4pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель в соответствующей форме на сайте </span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz</span></span></span></a> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">может выбрать сумму регулярного списания из предлагаемых вариантов или внести свою.</span></span></span></span></span></span></span></li>\r\n		<li style=\"margin-top:.15pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:40.8pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Поручение считается оформленным после успешного завершения первого списания с карты и получения уведомления об успешном списании на электронный адрес Благотворителя, указанный при оформлении поручения.</span></span></span></span></span></span></span></li>\r\n		<li style=\"margin-top:.2pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:46.8pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Поручение на регулярное списание действует до окончания срока действия карты владельца или до подачи Благотворителем письменного уведомления о прекращении действия поручения на электронный адрес Благополучателя </span></span><a href=\"mailto:info@waqyp.kz\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">info@waqyp.kz</span></span></span></a> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">не менее, чем за 15 календарных дней до даты очередного автоматического списания. Уведомление должно содержать следующие данные: фамилия и имя Благотворителя, как указано на карте, с которой осуществляется автоматическое списание средств; четыре последних цифры карты; электронный адрес, на который Благотворитель получит подтверждение о прекращении регулярного списания.</span></span></span></span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li style=\"margin-top:.5pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель дает разрешение на обработку и хранение своих персональных данных, используемых Благополучателем исключительно для исполнения указанного договора. Благополучатель обязуется не раскрывать третьим лицам личную и контактную информацию Благотворителя без его согласия.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.3pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благополучатель принимает благотворительное пожертвование с благодарностью.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.1pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Настоящая оферта вступает в силу со дня ее&nbsp; публикации&nbsp; на&nbsp; официальном </span></span><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">сайте </span></span><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">Корпоративного благотворительный фонд &laquo;Казахстанский фонд &laquo;УАҚЫП&raquo;</span></span></span></i></span></span></span></span></span></li>\r\n</ol>\r\n\r\n<p class=\"MsoBodyText\" style=\"margin-top:.05pt; margin-right:6.35pt; margin-bottom:.0001pt; margin-left:5.85pt; margin:0cm 0cm 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:12pt\"><span style=\"line-height:116%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">&ndash; <a href=\"http://www.waqyp.kz/\"><span style=\"color:blue\">www.waqyp.kz</span><span style=\"text-decoration:none\"><span style=\"text-underline:none\">.</span></span></a> Условия настоящей оферты также могут быть опубликованы и&nbsp;&nbsp;&nbsp;&nbsp; в других средствах массовой информации.</span></span></span></span></p>\r\n\r\n<ol start=\"16\">\r\n	<li style=\"margin-top:.15pt; margin-right:6.1pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Настоящая оферта является бессрочной и действует до дня, следующего за днем размещения на официальном сайте Благополучателя и в других средствах массовой информации извещения об отмене настоящей оферты.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.2pt; margin-right:6.0pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Условия настоящей оферты могут быть изменены Благополучателем без предварительного уведомления и действуют со дня, следующего за днем их размещения на официальном сайте Благополучателя </span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz</span></span></span><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"text-decoration:none\"><span style=\"text-underline:none\">,</span></span></span></span></a><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"> а также других средствах массовой информации.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.2pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Настоящий договор составлен в единственном экземпляре и опубликован Благополучателем на сайте </span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz,</span></span></span></a> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">как публичная оферта в соответствии со ст. 395 Гражданского кодекса Республики Казахстан.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.15pt; margin-right:6.0pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Реквизиты для перечисления денежных средств, а также&nbsp; возможные способы перечисления благотворительного пожертвования публикуются ниже, а также на официальном сайте Благополучателя и в других средствах массовой информации.</span></span></span></span></span></span></span></li>\r\n</ol>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol start=\"20\">\r\n	<li align=\"left\" style=\"margin-top:4.45pt; margin-right:0cm; margin-bottom:.0001pt; text-align:left; margin:0.25pt 6.05pt 0.0001pt 5.85pt\"><span style=\"font-size:11pt\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\">Адрес и реквизиты Благополучателя:</span></span></i></span></span></span></li>\r\n</ol>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:2.4pt; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Корпоративный благотворительный фонд &laquo;Казахстанский фонд &laquo;УАҚЫП&raquo;</span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:2.5pt; margin-right:6.25pt; margin-bottom:.0001pt; margin-left:5.85pt; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span style=\"line-height:102%\"><span style=\"tab-stops:103.4pt 152.1pt 232.0pt 308.85pt 328.85pt 385.85pt 433.2pt 482.85pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Юридический&nbsp;&nbsp;&nbsp;&nbsp; адрес:&nbsp;&nbsp;&nbsp;&nbsp; Республика&nbsp;&nbsp;&nbsp; Казахстан,&nbsp;&nbsp;&nbsp;&nbsp; г.&nbsp;&nbsp; Астана,&nbsp;&nbsp;&nbsp; район&nbsp;&nbsp;&nbsp;&nbsp; Есиль,&nbsp;&nbsp;&nbsp; пр. Кабанбай батыра, дом 28, офис 5.</span></span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-right:208.65pt; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span style=\"line-height:101%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Президент Фонда Айтбай Қайрат Орынбайұлы. БИН 170740000147</span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">КБе 18</span></span></p>\r\n\r\n<p class=\"MsoBodyText\" style=\"margin-top:.1pt; margin-right:282.55pt; margin-bottom:.0001pt; margin-left:5.85pt; margin:0cm 0cm 0.0001pt 5.85pt; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"line-height:101%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">ИИК KZ136010111000051511 (KZT) ИИК KZ746010111000051771 (RUB) ИИК KZ316010111000051769 (USD) ИИК KZ046010111000051770 (EUR) БИК HSBKKZKX</span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span style=\"line-height:13.75pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">АО &laquo;Народный Банк Казахстана&raquo;, Астанинский региональный филиал.</span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:.2pt; margin-right:0cm; margin-bottom:.0001pt; margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span style=\"tab-stops:41.7pt 128.5pt 149.85pt 245.6pt 281.05pt 301.8pt 387.5pt 451.9pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Для&nbsp;&nbsp;&nbsp;&nbsp; физических&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; и&nbsp;&nbsp;&nbsp;&nbsp; юридических&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; лиц&nbsp;&nbsp;&nbsp;&nbsp; в&nbsp;&nbsp;&nbsp;&nbsp; назначении&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; платежа&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; указать:</span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:2.45pt; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">&laquo;благотворительное пожертвование на уставные цели&raquo;.</span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:.25pt; margin-right:0cm; margin-bottom:.0001pt; margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:5.85pt; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:11pt\"><span style=\"tab-stops:418.35pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\"><span style=\"letter-spacing:-.05pt\">Президен</span></span></span></i><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\">т <span style=\"letter-spacing:-.05pt\">КБ</span>Ф &laquo;К<span style=\"letter-spacing:-.15pt\">а</span>захстанс<span style=\"letter-spacing:.1pt\">к</span>ий <span style=\"letter-spacing:-.05pt\">фон</span>д &laquo;У<span style=\"letter-spacing:.45pt\">А</span><span style=\"letter-spacing:-.65pt\">Қ</span><span style=\"letter-spacing:.1pt\">ЫП</span>&raquo;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"letter-spacing:-.05pt\">Айтб</span><span style=\"letter-spacing:.1pt\">а</span>й <span style=\"letter-spacing:-.05pt\">К.О.</span></span></span></i></span></span></span></p>', '<div class=\"WordSection1\">\r\n<p align=\"center\" style=\"margin-top:4.45pt; margin-right:186.45pt; margin-bottom:.0001pt; margin-left:186.2pt; text-align:center; margin:0cm 0cm 0.0001pt\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\">ПУБЛИЧНАЯ ОФЕРТА</span></span></i></span></span></span></p>\r\n\r\n<p style=\"margin:0cm 0cm 0.0001pt; text-align:center\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\">о добровольном благотворительном пожертвовании</span></span></i></span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:.2pt; margin-right:0cm; margin-bottom:.0001pt; margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<p style=\"margin-top:0cm; margin-right:5.95pt; margin-bottom:.0001pt; margin-left:5.85pt; text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\"><span style=\"letter-spacing:-.05pt\">Корпора</span></span></span></span></i><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\"><span style=\"letter-spacing:.1pt\">т</span></span></span></span></i><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">ивный</span></span></span></i><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\"> &nbsp;<span style=\"letter-spacing:.05pt\">б</span><span style=\"letter-spacing:-.05pt\">лаготвори</span><span style=\"letter-spacing:.1pt\">т</span>ельный &nbsp;<span style=\"letter-spacing:-.05pt\">фон</span>д &nbsp;<span style=\"letter-spacing:.1pt\">&laquo;</span><span style=\"letter-spacing:-.05pt\">Казахстански</span>й &nbsp;<span style=\"letter-spacing:-.05pt\">фон</span>д &nbsp;<span style=\"letter-spacing:.1pt\">&laquo;</span><span style=\"letter-spacing:-.05pt\">У</span><span style=\"letter-spacing:.45pt\">А</span><span style=\"letter-spacing:-.65pt\">Қ</span><span style=\"letter-spacing:.1pt\">ЫП</span>&raquo;</span></span></span></i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">,</span></span> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">в </span></span><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">лице Президента Айтбай Қайрата Орынбайұлы, действующего на основании Устава, именуемый в дальнейшем </span></span><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">&laquo;Благополучатель&raquo; </span></span></span></i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">публикует настоящий договор, являющийся публичным договором-офертой (предложением) и предлагает неопределенному кругу лиц (физическим и юридическим лицам), именуемым в дальнейшем </span></span><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">&laquo;Благотворитель&raquo;, </span></span></span></i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">заключить договор о добровольном благотворительном пожертвовании на нижеследующих условиях:</span></span></span></span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:.4pt; margin-right:0cm; margin-bottom:.0001pt; margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"margin-top:0cm; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель по настоящему договору безвозмездно передает Благополучателю денежные средства (благотворительное пожертвование) на благотворительные цели, в том числе на цели, указанные в пунктах 2, 3 настоящего договора.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-right:6.2pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Участие Благотворителя в благотворительной деятельности направлено на поддержку благотворительной деятельности Благополучателя.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.1pt; margin-right:5.8pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворительное пожертвование используется Благополучателем в благотворительных целях, определенных Уставом и Благотворительной программой Благополучателя, а также в целях обеспечения административно- хозяйственной деятельности Благополучателя.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благополучатель публикует информацию о своей работе, целях и задачах, мероприятиях и результатах на официальном сайте </span></span><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">Корпоративного благотворительного фонда&nbsp; &laquo;Казахстанский&nbsp; фонд&nbsp; &laquo;УАҚЫП&raquo;&nbsp; </span></span></span></i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">&ndash;<u style=\"text-underline:blue\"> </u></span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz</span></span></span> </a><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">и/или в других открытых источниках.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.15pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Датой полного и безоговорочного акцепта (принятия предложения) настоящей оферты является дата поступления денежных средств Благотворителя на расчетный счет Благополучателя либо, в соответствующих случаях, на счет Благополучателя в платежной системе; а также дата отправки короткого текстового сообщения (SMS).</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Акцепт данного предложения Благотворителем означает, что Благотворитель ознакомился и полностью согласен со всеми положениями и условиями&nbsp; настоящего предложения, и равносилен заключению договора о добровольном благотворительном пожертвовании.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-right:5.95pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Принимая условия данного предложения, Благотворитель подтверждает добровольный и безвозмездный характер благотворительного пожертвования.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.1pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Моментом заключения договора и вступления его в силу считается момент поступления на расчетный счет&nbsp; Благополучателя&nbsp; перечисленных&nbsp; Благотворителем денежных средств в качестве благотворительного&nbsp; пожертвования.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-right:5.95pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благополучатель готов заключать&nbsp; договоры&nbsp; пожертвования&nbsp; в&nbsp; ином&nbsp; порядке и (или) на иных условиях, нежели это предусмотрено&nbsp; настоящей&nbsp; офертой, для чего любое заинтересованное лицо вправе обратиться для заключения соответствующего договора в офис Благополучателя.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-right:6.15pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection1\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Размер безвозмездно передаваемых Благотворителем денежных средств (благотворительного пожертвования), определяется Благотворителем самостоятельно в одностороннем порядке на&nbsp; свое&nbsp; усмотрение&nbsp; и&nbsp; без&nbsp; согласования с Благополучателем.</span></span></span></span></span></span></span></li>\r\n</ol>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"WordSection2\">\r\n<ol start=\"11\">\r\n	<li style=\"margin-top:4.5pt; margin-right:6.1pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt 161.05pt 254.65pt 407.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; передает&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; благотворительное&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; пожертвование Благополучателю, указанное в п.1 настоящего договора добровольно, бескорыстно и на безвозвратной основе.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.2pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель может оформить на сайте </span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz</span></span></span></a> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">поручение на регулярное (ежемесячное) списание пожертвований с банковской карты:</span></span></span></span></span></span></span>\r\n	<ol>\r\n		<li style=\"margin-top:.1pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:41.4pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель в соответствующей форме на сайте </span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz</span></span></span></a> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">может выбрать сумму регулярного списания из предлагаемых вариантов или внести свою.</span></span></span></span></span></span></span></li>\r\n		<li style=\"margin-top:.15pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:40.8pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Поручение считается оформленным после успешного завершения первого списания с карты и получения уведомления об успешном списании на электронный адрес Благотворителя, указанный при оформлении поручения.</span></span></span></span></span></span></span></li>\r\n		<li style=\"margin-top:.2pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:46.8pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Поручение на регулярное списание действует до окончания срока действия карты владельца или до подачи Благотворителем письменного уведомления о прекращении действия поручения на электронный адрес Благополучателя </span></span><a href=\"mailto:info@waqyp.kz\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">info@waqyp.kz</span></span></span></a> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">не менее, чем за 15 календарных дней до даты очередного автоматического списания. Уведомление должно содержать следующие данные: фамилия и имя Благотворителя, как указано на карте, с которой осуществляется автоматическое списание средств; четыре последних цифры карты; электронный адрес, на который Благотворитель получит подтверждение о прекращении регулярного списания.</span></span></span></span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li style=\"margin-top:.5pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благотворитель дает разрешение на обработку и хранение своих персональных данных, используемых Благополучателем исключительно для исполнения указанного договора. Благополучатель обязуется не раскрывать третьим лицам личную и контактную информацию Благотворителя без его согласия.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.3pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Благополучатель принимает благотворительное пожертвование с благодарностью.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.1pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Настоящая оферта вступает в силу со дня ее&nbsp; публикации&nbsp; на&nbsp; официальном </span></span><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">сайте </span></span><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span arial=\"\" style=\"font-family:\">Корпоративного благотворительный фонд &laquo;Казахстанский фонд &laquo;УАҚЫП&raquo;</span></span></span></i></span></span></span></span></span></li>\r\n</ol>\r\n\r\n<p class=\"MsoBodyText\" style=\"margin-top:.05pt; margin-right:6.35pt; margin-bottom:.0001pt; margin-left:5.85pt; margin:0cm 0cm 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:12pt\"><span style=\"line-height:116%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">&ndash; <a href=\"http://www.waqyp.kz/\"><span style=\"color:blue\">www.waqyp.kz</span><span style=\"text-decoration:none\"><span style=\"text-underline:none\">.</span></span></a> Условия настоящей оферты также могут быть опубликованы и&nbsp;&nbsp;&nbsp;&nbsp; в других средствах массовой информации.</span></span></span></span></p>\r\n\r\n<ol start=\"16\">\r\n	<li style=\"margin-top:.15pt; margin-right:6.1pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Настоящая оферта является бессрочной и действует до дня, следующего за днем размещения на официальном сайте Благополучателя и в других средствах массовой информации извещения об отмене настоящей оферты.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.2pt; margin-right:6.0pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Условия настоящей оферты могут быть изменены Благополучателем без предварительного уведомления и действуют со дня, следующего за днем их размещения на официальном сайте Благополучателя </span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz</span></span></span><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"text-decoration:none\"><span style=\"text-underline:none\">,</span></span></span></span></a><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\"> а также других средствах массовой информации.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.2pt; margin-right:5.95pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Настоящий договор составлен в единственном экземпляре и опубликован Благополучателем на сайте </span></span><a href=\"http://www.waqyp.kz/\"><span style=\"font-size:12.0pt\"><span style=\"line-height:116%\"><span style=\"color:blue\">www.waqyp.kz,</span></span></span></a> <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">как публичная оферта в соответствии со ст. 395 Гражданского кодекса Республики Казахстан.</span></span></span></span></span></span></span></li>\r\n	<li style=\"margin-top:.15pt; margin-right:6.0pt; margin-bottom:.0001pt; margin:0.25pt 6.05pt 0.0001pt 5.85pt; text-align:justify\"><span style=\"page:WordSection2\"><span style=\"font-size:11pt\"><span style=\"line-height:116%\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:116%\">Реквизиты для перечисления денежных средств, а также&nbsp; возможные способы перечисления благотворительного пожертвования публикуются ниже, а также на официальном сайте Благополучателя и в других средствах массовой информации.</span></span></span></span></span></span></span></li>\r\n</ol>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol start=\"20\">\r\n	<li align=\"left\" style=\"margin-top:4.45pt; margin-right:0cm; margin-bottom:.0001pt; text-align:left; margin:0.25pt 6.05pt 0.0001pt 5.85pt\"><span style=\"font-size:11pt\"><span style=\"tab-stops:27.25pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\">Адрес и реквизиты Благополучателя:</span></span></i></span></span></span></li>\r\n</ol>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:2.4pt; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Корпоративный благотворительный фонд &laquo;Казахстанский фонд &laquo;УАҚЫП&raquo;</span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:2.5pt; margin-right:6.25pt; margin-bottom:.0001pt; margin-left:5.85pt; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span style=\"line-height:102%\"><span style=\"tab-stops:103.4pt 152.1pt 232.0pt 308.85pt 328.85pt 385.85pt 433.2pt 482.85pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Юридический&nbsp;&nbsp;&nbsp;&nbsp; адрес:&nbsp;&nbsp;&nbsp;&nbsp; Республика&nbsp;&nbsp;&nbsp; Казахстан,&nbsp;&nbsp;&nbsp;&nbsp; г.&nbsp;&nbsp; Астана,&nbsp;&nbsp;&nbsp; район&nbsp;&nbsp;&nbsp;&nbsp; Есиль,&nbsp;&nbsp;&nbsp; пр. Кабанбай батыра, дом 28, офис 5.</span></span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-right:208.65pt; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span style=\"line-height:101%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Президент Фонда Айтбай Қайрат Орынбайұлы. БИН 170740000147</span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">КБе 18</span></span></p>\r\n\r\n<p class=\"MsoBodyText\" style=\"margin-top:.1pt; margin-right:282.55pt; margin-bottom:.0001pt; margin-left:5.85pt; margin:0cm 0cm 0.0001pt 5.85pt; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"line-height:101%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">ИИК KZ136010111000051511 (KZT) ИИК KZ746010111000051771 (RUB) ИИК KZ316010111000051769 (USD) ИИК KZ046010111000051770 (EUR) БИК HSBKKZKX</span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span style=\"line-height:13.75pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">АО &laquo;Народный Банк Казахстана&raquo;, Астанинский региональный филиал.</span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:.2pt; margin-right:0cm; margin-bottom:.0001pt; margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span style=\"tab-stops:41.7pt 128.5pt 149.85pt 245.6pt 281.05pt 301.8pt 387.5pt 451.9pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Для&nbsp;&nbsp;&nbsp;&nbsp; физических&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; и&nbsp;&nbsp;&nbsp;&nbsp; юридических&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; лиц&nbsp;&nbsp;&nbsp;&nbsp; в&nbsp;&nbsp;&nbsp;&nbsp; назначении&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; платежа&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; указать:</span></span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:2.45pt; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">&laquo;благотворительное пожертвование на уставные цели&raquo;.</span></span></p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<p align=\"left\" class=\"MsoBodyText\" style=\"margin-top:.25pt; margin-right:0cm; margin-bottom:.0001pt; margin-left:0cm; text-align:left; margin:0cm 0cm 0.0001pt 5.85pt\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:5.85pt; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:11pt\"><span style=\"tab-stops:418.35pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\"><span style=\"letter-spacing:-.05pt\">Президен</span></span></span></i><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span arial=\"\" style=\"font-family:\">т <span style=\"letter-spacing:-.05pt\">КБ</span>Ф &laquo;К<span style=\"letter-spacing:-.15pt\">а</span>захстанс<span style=\"letter-spacing:.1pt\">к</span>ий <span style=\"letter-spacing:-.05pt\">фон</span>д &laquo;У<span style=\"letter-spacing:.45pt\">А</span><span style=\"letter-spacing:-.65pt\">Қ</span><span style=\"letter-spacing:.1pt\">ЫП</span>&raquo;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"letter-spacing:-.05pt\">Айтб</span><span style=\"letter-spacing:.1pt\">а</span>й <span style=\"letter-spacing:-.05pt\">К.О.</span></span></span></i></span></span></span></p>', 1, '2019-05-30 06:59:00', '2019-06-04 13:55:04');

