  `title_kk` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `title_en` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `desc_ru` varchar(500) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `desc_kk` varchar(500) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `desc_en` varchar(500) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `status` int(11) NOT NULL DEFAULT '1',
  `created_at` timestamp NULL DEFAULT NULL,
  `img` longtext COLLATE utf8mb4_unicode_ci,
  `type` int(11) DEFAULT '1',
  `updated_at` timestamp NULL DEFAULT NULL,
  `is_main` tinyint(3) UNSIGNED NOT NULL DEFAULT '0',
  `is_archive` tinyint(4) DEFAULT '0',
  `sort` int(11) DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

