INSERT INTO `reports` (`id`, `sum_ru`, `sum_en`, `sum_kk`, `recipient_ru`, `recipient_kk`, `recipient_en`, `helped_ru`, `helped_kk`, `helped_en`, `target_ru`, `target_kk`, `target_en`, `report_category_id`, `created_at`, `updated_at`, `status`, `sort`) VALUES
(1, '1 750 000 тенге', '1 750 000 тенге', '1 750 000 тенге', '250 малоимущих семей', '250 малоимущих семей', '250 малоимущих семей', 'Предприниматели г. Алматы', 'Предприниматели г. Алматы', 'Предприниматели г. Алматы', 'Совмесно с центральной мечетью г.Алматы, ОФ «Үммет» проведена акция «Мектеп сөмкесі»: розданы летние одежды и школьные сумки с принадлежностями в размере 250 шт.', 'Совмесно с центральной мечетью г.Алматы, ОФ «Үммет» проведена акция «Мектеп сөмкесі»: розданы летние одежды и школьные сумки с принадлежностями в размере 250 шт.', 'Совмесно с центральной мечетью г.Алматы, ОФ «Үммет» проведена акция «Мектеп сөмкесі»: розданы летние одежды и школьные сумки с принадлежностями в размере 250 шт.', 1, '2019-05-01 14:21:00', '2019-05-01 20:22:07', 1, 0),
(3, '800 000 тенге', '800 000 тенге', '800 000 тенге', 'Федерация Казахстанского футбола', 'Федерация Казахстанского футбола', 'Федерация Казахстанского футбола', 'через спортсменов', 'через спортсменов', 'через спортсменов', 'Оказана волонтерская помощь в организации и проведении футбольных мачтей сборной молодежной Казахстана (до 21 года) 23 и 27 марта т.г.', 'Оказана волонтерская помощь в организации и проведении футбольных мачтей сборной молодежной Казахстана (до 21 года) 23 и 27 марта т.г.', 'Оказана волонтерская помощь в организации и проведении футбольных мачтей сборной молодежной Казахстана (до 21 года) 23 и 27 марта т.г.', 1, '2019-05-22 23:59:00', '2019-05-23 06:00:00', 1, 0),
(4, '3 600 000 тенге', '3 600 000 тенге', '3 600 000 тенге', 'Малоимущие семьи в селах', 'Малоимущие семьи в селах', 'Малоимущие семьи в селах', 'Предпринимателей ЮКО', 'Предпринимателей ЮКО', 'Предпринимателей ЮКО', 'В целях воспитания детей, а также получения дохода в малоимущих семьях в трех аулах Южно-Казахстанской области проведена благотворительная акция «проект 7 тана» и розданы 21 коровы.', 'В целях воспитания детей, а также получения дохода в малоимущих семьях в трех аулах Южно-Казахстанской области проведена благотворительная акция «проект 7 тана» и розданы 21 коровы.', 'В целях воспитания детей, а также получения дохода в малоимущих семьях в трех аулах Южно-Казахстанской области проведена благотворительная акция «проект 7 тана» и розданы 21 коровы.', 1, '2019-05-23 00:00:00', '2019-05-23 06:01:30', 1, 0),
(5, '1 500 000 тенге', '1 500 000 тенге', '1 500 000 тенге', 'Малоимущие семья и дети инвалиды', 'Малоимущие семья и дети инвалиды', 'Малоимущие семья и дети инвалиды', 'Предпринимателей ЮКО (г. Туркестан)', 'Предпринимателей ЮКО (г. Туркестан)', 'Предпринимателей ЮКО (г. Туркестан)', 'Совместно с предпринимателями и имамами г.Туркестан проведены мероприятия по оказанию благотворительной материальной помощи малоимущим семьям и детям инвалидам, имамами проведены проповеди об этикете и моральных устоях в Исламе.', 'Совместно с предпринимателями и имамами г.Туркестан проведены мероприятия по оказанию благотворительной материальной помощи малоимущим семьям и детям инвалидам, имамами проведены проповеди об этикете и моральных устоях в Исламе.', 'Совместно с предпринимателями и имамами г.Туркестан проведены мероприятия по оказанию благотворительной материальной помощи малоимущим семьям и детям инвалидам, имамами проведены проповеди об этикете и моральных устоях в Исламе.', 1, '2019-05-23 00:01:00', '2019-05-23 06:02:33', 1, 0),
(6, '500 000 тенге', '500 000 тенге', '500 000 тенге', 'Студенты и молодежь Казахстана', 'Студенты и молодежь Казахстана', 'Студенты и молодежь Казахстана', 'Спортсмены г. Алматы', 'Спортсмены г. Алматы', 'Спортсмены г. Алматы', 'Совместно с Kilem Market университета Международного бизнеса оказана поддержка в проведении Баскетбольного турнира «Наш турнир», организованного «Kazakhstan Can Jump» 14-15 апреля т.г.', 'Совместно с Kilem Market университета Международного бизнеса оказана поддержка в проведении Баскетбольного турнира «Наш турнир», организованного «Kazakhstan Can Jump» 14-15 апреля т.г.', 'Совместно с Kilem Market университета Международного бизнеса оказана поддержка в проведении Баскетбольного турнира «Наш турнир», организованного «Kazakhstan Can Jump» 14-15 апреля т.г.', 1, '2019-05-23 00:02:00', '2019-05-23 06:03:28', 1, 0),
(7, '300 000 тенге', '300 000 тенге', '300 000 тенге', 'Азизбекова А.', 'Азизбекова А.', 'Азизбекова А.', 'Спонсоры', 'Спонсоры', 'Спонсоры', 'Оказана помощь казахстанской спортсменке (10 лет), которая приняла участие на международном соревновании в Узбекистане по художественной гимнастике и заняла первое место.', 'Оказана помощь казахстанской спортсменке (10 лет), которая приняла участие на международном соревновании в Узбекистане по художественной гимнастике и заняла первое место.', 'Оказана помощь казахстанской спортсменке (10 лет), которая приняла участие на международном соревновании в Узбекистане по художественной гимнастике и заняла первое место.', 1, '2019-05-23 00:03:00', '2019-05-23 06:04:18', 1, 0);

